css:
@-moz-document domain("mega.nz"), domain("mega.co.nz"), domain("mega")
{
.default-white-button:not(.top-login-button),
.chat-right-area::after,
.bottom-page.plans-info,
.create-folder-input-bl > input,
div.create-folder-button-cancel.popup.default-grey-button.right {
    background: #333;
}

body,
.loading-mid-white-block,
.light-overlay,
.top-menu-popup,
.dropdown-item:not(.static):hover,
.contains-submenu.opened,
.dropdown.body,
.shared-top-details,
.shared-details-buttons,
.shared-details-buttons::before,
.transfer-table-header,
.transfer-table-wrapper,
.fm-empty-folder,
.fm-left-panel,
.jspContainer,
.default-grey-button,
.widgets.content-block,
.content-panel.conversations.active,
.messages-block,
.arc-conversation-btn-block:hover,
.messages-textarea,
.fm-empty-contacts,
.bottom-page.white-block,
.vertical-centered-bl,
.plans-block > .membership-pad-bl,
.sign .main-pad-block,
.pages-nav.content-block,
.vertical-centered-cell > .pages-nav.content-block,
.create-folder-input-bl,
.create-folder-bottom  {
    background: #222;
}

.fm-right-header,
.fm-header-buttons,
.data-block-view:before,
.file-settings-icon,
.top-search-input::selection,
.notification-header,
.fm-notifications-bottom,
.grid-url-arrow,
.fm-transfers-header,
.account.tabs-bl,
.contacts-tabs-bl,
.fm-dialog,
.fm-account-input,
.medium.account.grey-info-bl,
.account.widget,
.fm-account-history-head,
.fm-account-history .grey-bar,
.grid-table tr:hover td,
.grid-table tr:hover:nth-child(2n+1) td,
.chat-right-pad,
.arc-conversation-btn-block,
.conversations-pane li:nth-child(2n+1), .arc-chat-messages-block tr.arc-chat-list:nth-child(2n+1),
.chat-textarea,
.toast-notification,
.contact-top-details,
.bottom-page.grey-block,
.bottom-menu.body{
    background: #111;
}

.arrow:not(.grid-header-star) {
    background: none;
}

.notification-txt,
.grid-table.sessions > tbody > tr span.current-session-txt,
.grid-table.sessions > tbody > tr span.active-session-txt {
    color: #222;
}

.bottom-page > .top-head,
.top-head,
.submenu,
.fm-left-menu.transfers,
.top-head.floating.activated{
    background: #D90007;
}

.settings-logout,
#login-name::selection,
#login-name2::selection,
#login-password::selection,
#login-password2::selection {
    background: #772225
}

a.st-facebook-button{
    color: #D90007;
}

ul.loading-info > li,
.dropdown-item,
.button,
.nw-fm-tree-folder,
.data-item-icon,
.file-block-title,
.fm-breadcrumbs.has-next-button .right-arrow-bg,
.fm-tree-panel,
.nw-tree-panel-header,
.notification-username,
.shared-details-folder-name,
.membership-big-txt.email,
.account.membership-plan,
.account.tab-lnk,
.account.data-block.storage-data > .used-space > span,
.account.progress-title,
.grid-table.sessions > tbody *,
.account > .radio-txt,
.account.left-pane.big-txt,
.account.left-pane.info-txt,
.chat-right-pad *,
.arc-conversation-btn-block:hover,
.conversation-message,
textarea.messages-textarea,
.contact-top-details *,
.horizontal-centered-bl > .bottom-page.top-header,
.bottom-page.plans-info,
.account.top-header
{
    color: #bbb;
}

.link-button:hover,
.menu,
.top-menu-item:hover,
.top-search-input,
.transfer-table-header > tbody > tr > th,
.membership-big-txt.name,
.fm-account-header,
.fm-account-input input,
.default-dropdown-item,
.account.storage,
.account.plan-info,
.account.data-txt,
table.grid-table > tr:hover > td > div > .shared-folder-name,
table.grid-table > tr:hover > td > div > .fm-chat-user,
table.grid-table > tr:hover > td > div > .fm-chat-user-status,
.user-card-name.conversation-name,
#login-name,
#login-name2,
#login-password,
#login-password2,
.nw-contact-item > .nw-contact-name,
.fm-empty-pad > .fm-empty-cloud-txt,
.dropdown.body.tooltip.small,
h5.main-italic-header,
.expiry{
    color: #ddd;
}

a.user-name,
.account.tab-lnk.active,
.contacts-tab-lnk.active,
#token-input-add-user-dialog-input,
.top-change-language-name,
.fm-notifications-bottom > .default-white-button,
.create-folder-input-bl > input,
.create-folder-button {
    color: #fff;
}

.avatar-bg.colorized,
.mega-logo  {
    filter: grayscale(100%) invert(90%);    
}

.grid-header-star,
.fm-files-view-icon,
.transfer-filetype-icon,
.arrow.desc,
.arrow.asc,
.data-item-icon,
.top-clear-button,
.notification-txt,
.shared-folder-icon,
table.grid-table > tr > td > .avatar-wrapper,
.fm-breadcrumbs,
.transfer-type,
.transfer-table-header > tbody > tr > th:after,
table.grid-table.contacts > tr:hover,
.tranfer-filetype-txt:after,
.membership-pad-bl,
.reg-st3-membership-icon,
.pro-feature-icon,
.period{
    filter: invert(100%);
}

.fm-account-select-block > div,
.account.chart-block,
.shared-with-me > tr:hover,
.toast-close-button{
    filter: invert(94%);
}

.grid-scrolling-table,
.transfer-scrolling-table,
.small-icon,
.nw-fm-arrow-icon,
.dropdown-item.contains-submenu:not(.disabled-submenu)::before,
.fm-breadcrumbs.has-next-button,
.fm-account-button,
.avatar-white-bg,
.account-history-dropdown-button,
.account-history-dropdown,
.account-history-drop-items.active,
.start-chat-button{
    filter: invert(88%);
}

.grey-rocket {
    filter: invert(86.5%);
}
.jspContainer:before,
.jspContainer:after,
.top-menu-item.submenu-item::before,
.grid-header-star,
.grid-url-arrow,
.nw-tree-panel-arrows,
.account-icon{
    filter: invert(85%);
}

.dropdown-section > hr,
.dropdown-white-arrow,
.fm-empty-transfers-bg,
.fm-empty-folder-bg,
.fm-empty-contacts-bg,
.top-login-forgot-pass {
    filter: invert(50%);
}

ul > li > span:hover > .nw-fm-arrow-icon,
ul > li > span:hover > .data-item-icon,
ul > li > .nw-fm-tree-item.selected > .data-item-icon,
.dropdown-item.contains-submenu.opened:before,
.dropdown-item:hover > i.small-icon,
.dropdown-item.opened > i.small-icon,
.membership,
table.grid-table > tr:hover > td > .avatar-wrapper,
table.grid-table > tr:hover > td > .shared-folder-icon,
table.grid-table.contacts > tr:hover > td > .avatar-wrapper,
.top-login-forgot-pass:hover {
    filter: invert(0);
}

a.fm-avatar {
    filter: brightness(80%);
}

.top-icon{
    filter: brightness(250%);
}

.fm-tree-panel,
.nw-tree-panel-header {
    border-left: 1px #333 solid;
    border-right: 1px #333 solid;
    background: #222;
}

.dropdown,
.default-select-dropdown,
.account.left-pane.divider
.chat-right-pad {
    border: 1px solid #444;
}

.account.tabs-bl,
.contacts-tabs-bl,
a.nlanguage-lnk{
    border-color: #333;
}

.grid-table-header,
.file-block-scrolling {
    border-top: 1px #333 solid;
    border-bottom: 1px #333 solid;
    background: #222;
}

.logo {
    border: 1px solid #fff;
}

.dropdown.body.submenu {
    margin-left: 0px;
}

.fm-notifications-bottom .default-white-button {
    margin-top: 0px;
}

.arrow.desc {
    background-image: url(https://eu.static.mega.co.nz/3/images/mega/fm-main-sprite_v4.png);
    background-position: right -1285px;
    background-repeat: no-repeat;
}

.arrow.asc {
    background-image: url(https://eu.static.mega.co.nz/3/images/mega/fm-main-sprite_v4.png);
    background-position: right -1225px;
    background-repeat: no-repeat;
}
}
