@-moz-document domain("www.luogu.org") {
.am-btn, .am-badge, .am-panel, .tip-twitter, .am-radius {
    /* 删除让人浮躁的圆角 */
    /* 希望各位 OIer 不要被掰弯 */
    border-radius: 0px!important;
}
.am-badge {
    /* badage 美化 */
    font-weight: 500;
    font-size: 10px;
    padding-top: 5px;
    padding-bottom: 5px;
}
.am-comment-primary .am-comment-main,
.am-comment-primary .am-comment-main:before,
.am-comment-primary .am-comment-avatar {
    /* 删除犇犇和讨论的蓝框框 */
    border: none;
}
}

@-moz-document domain("www.luogu.org") {
/* === 美化 sidebar === */

@media screen and (max-width: 700px) {
    .lg-footer {
        background: #333;
    }
}
@media screen and (min-width: 700px) {
    /* 强行在左边给 sidebar 挤一块位置出来 */
    .lg-sidebar {
        max-width: 60px;
    }
    .lg-content {
        margin-left: 60px;
    }
    div.am-topbar-brand {
        display: none;
    }
    .am-topbar {
        padding-left: 50px;
        margin-left: 60px;
    }
    .lg-sidebar {
        margin-top: -51px;
    }
    .lg-rail-sidebar {
        max-width: 20px;
        width: 20px;
    }
    .lg-footer {
        padding-bottom: 0px;
        background: #34495e;
    }
    .lg-footer > .lg-footer-top {
        display: none;
    }
    .lg-footer > .lg-index-tool {
        margin-left: 60px;
        max-width: calc(100% - 65px);
    }
    .lg-footer > .lg-index-tool {
        background: #333;
    }
    .lg-footer > .lg-index-tool {
        padding-left: 50px;
        padding-right: 50px;
        margin-top: 0px;
        padding-top: 30px;
        padding-bottom: 30px;
    }
}
}

@-moz-document domain("www.luogu.org") {
/* === 颜色相关 === */

.am-topbar, .am-collapse, .am-dropdown-toggle, .am-topbar-form, .topbar-search-text, #topbar-search, .lg-header-list>li {
    /* header 栏颜色 */
    background: #333;
    border-color: #333;
}
#topbar-search-text {
    /* header 搜索框消除 border */
    border-color: #00000000;
}

.lg-nav-home, .lg-nav-problem, .lg-nav-training, .lg-nav-contest, .lg-nav-record, .lg-nav-discuss {
    /* 侧边栏按钮背景全透明 */
    background-color: #00000000!important;
}
.lg-nav-home {
    background-color: #5EB95E!important;
}

.am-panel-primary {
    /* border: none; */
    background-color: #00000000;
}

.am-selected
.am-dropdown
.am-active
.am-selected-content
.am-dropdown-content
.am-btn-secondary .am-dropdown-toggle,
.am-btn-secondary .am-active,
.am-btn-secondary:active {
    background-color: #5EB95E!important;
}
}

@-moz-document url-prefix("http://www.luogu.org/app/ide_frame"), url-prefix("https://www.luogu.org/app/ide_frame") {
.am-topbar {
    margin-left: 0px;
}
.am-topbar-brand a {
    color: #fff;
}
}
