css:
@-moz-document domain("manabasecrafter.com") {
/*/ FORSIDE /*/

/*/ bakgrunn /*/
body {
    font-family: sans-serif !important;
    background-color: #efefef;
    background-color: #2b253a;
    margin: 0;
    padding: 0;
    color: #efefef;
    min-height: 800px;
}
/*/ banner /*/
#active-menu {
    width: 100%;
    border-bottom: 2px solid #fdfdfd;
    background: #2b253a;
}
/*/ join us /*/
#social-area .social-share {
    font-family: sans-serif !important;
    color: #efefef;
    padding: 0;
    font-size: 17px;
    margin-top: 10px;
}
/*/ go mobile /*/
#switch-version a {
    background: #40355a;
    padding: 4px;
    border-radius: 3px;
    color: #fff;
    font-weight: bold;
    text-decoration: none;
}
/*/ browse by colors /*/
.search__index #search-container #search-content #search-query-box-wrap #search-query-box-carriage #color-selection .checkboxes .browse-text, .search__generate_permalink #search-container #search-content #search-query-box-wrap #search-query-box-carriage #color-selection .checkboxes .browse-text {
    font-size: 16px;
    color: #efefef;
    display: inline-block;
    margin-right: 15px;
}
/*/ browse by colors overskrift /*/
#commander-color-identity-name {
    color: #efefef;
    font-size: 20px;
}
/*/ browse by colors aktiv font /*/
.search__index #commander-preview-wrap #commander-preview-block .commander-wrap .commander-name, .search__generate_permalink #commander-preview-wrap #commander-preview-block .commander-wrap .commander-name {
    font-family: sans-serif;
    margin-top: 16px;
}
/*/ browse by colors aktiv font farge/*/
.search__index #commander-preview-wrap #commander-preview-block .commander-wrap .commander-name a, .search__generate_permalink #commander-preview-wrap #commander-preview-block .commander-wrap .commander-name a {
    color: #fbfbfb;
    text-decoration: none;
}
/*/ browse by colors aktiv hover /*/
.search__index #commander-preview-wrap #commander-preview-block .commander-wrap .commander-name a:hover, .search__generate_permalink #commander-preview-wrap #commander-preview-block .commander-wrap .commander-name a:hover {
    color: #8fbe00;
    text-decoration: underline;
}
/*/ browse by colors - no matching commanders /*/
.search__index #no-matching-commanders, .search__generate_permalink #no-matching-commanders {
    margin: 70px auto 0 auto;
    width: 816px;
    font-family: sans-serif !important;
    color: #fff;
    font-size: 42px;
    display: none;
}
/*/ trending commanders tittel /*/
.search__index #popular-commanders-block #popular-commanders-title, .search__generate_permalink #popular-commanders-block #popular-commanders-title {
    font-family: sans-serif !important;
    color: #efefef;
    font-size: 36px;
}
/*/ trending commanders bakgrunn /*/
.search__index #popular-commanders-block #popular-commanders, .search__generate_permalink #popular-commanders-block #popular-commanders {
    margin-top: 20px;
    margin-bottom: 400px;
    background-color: #40355a;
    background-image: url(http://google.com) !important;
    background-size: cover;
    -moz-border-radius: 14px;
    -webkit-border-radius: 14px;
    border-radius: 14px;
}
/*/ trending commanders font /*/
.search__index #popular-commanders-block #popular-commanders .popular-commander-wrap .popular-commander-title, .search__generate_permalink #popular-commanders-block #popular-commanders .popular-commander-wrap .popular-commander-title {
    font-family: sans-serif !important;
    text-align: center;
}
/*/ trending commanders font farge /*/
.search__index #popular-commanders-block #popular-commanders .popular-commander-wrap .popular-commander-title a, .search__generate_permalink #popular-commanders-block #popular-commanders .popular-commander-wrap .popular-commander-title a {
    color: #efefef;
    color: #fdfdfd;
    text-decoration: none;
}
/*/ trending commanders hover navn /*/
.search__index #popular-commanders-block #popular-commanders .popular-commander-wrap .popular-commander-title a:hover, .search__generate_permalink #popular-commanders-block #popular-commanders .popular-commander-wrap .popular-commander-title a:hover {
    color: #efefef;
    color: #8fbe00;
    text-decoration: underline;   
}
/*/ weekly trends font /*/
.search__index #popular-commanders-block #popular-commanders .stats-link-wrap a.stats-link, .search__generate_permalink #popular-commanders-block #popular-commanders .stats-link-wrap a.stats-link {
    text-decoration: none;
    font-family: sans-serif !important;
    font-size: 160%;
}
/*/ weekly trends font farge /*/
body a {
    color: #fefefe;
    text-decoration: none;
}
/*/ weekly trends hover /*/
.search__index #popular-commanders-block #popular-commanders .stats-link-wrap a.stats-link:hover, .search__generate_permalink #popular-commanders-block #popular-commanders .stats-link-wrap a.stats-link:hover {
    color: #8fbe00;
    text-decoration: underline;
}

/*/ SPESIFIKK COMMANDER /*/

/*/ Commander tittel /*/
body.search__results #commander-section #commander-name, body.search__permalink #commander-section #commander-name, body.search__commander #commander-section #commander-name {
    font-family: sans-serif !important;
    font-weight: bold;
    margin-bottom: 12px;
    font-size: 44px;
    text-align: center;
    color: #efefef;
    padding-top: 15px;
}
/*/ Popularity /*/
body.search__results #popularity-wrap th, body.search__permalink #popularity-wrap th, body.search__commander #popularity-wrap th {
    font-weight: 700;
    font-family: sans-serif;
}
body.search__results #popularity-wrap, body.search__permalink #popularity-wrap, body.search__commander #popularity-wrap {
    margin-bottom: 14px;
    color: #efefef;
    font-size: 13px;
    font-family: sans-serif;
}
body.search__results #popularity-wrap .duration, body.search__permalink #popularity-wrap .duration, body.search__commander #popularity-wrap .duration {
    padding-top: 0.5rem;
    font-family: sans-serif;
}
/*/ Jump-to banner bakgrunn /*/
#control-options-wrap {
    width: 100%;
    position: relative;
    overflow-y: hidden;
    z-index: 99;
    background-color: dimgray;
    background: #40355a !important;
    -moz-border-radius-bottom-left: 8px;
    -webkit-border-radius-bottom-left: 8px;
    border-bottom-left-radius: 8px;
    -moz-border-radius-bottom-right: 8px;
    -webkit-border-radius-bottom-right: 8px;
    border-bottom-right-radius: 8px;
    margin: 0 auto;
    max-width: 1024px;
    min-width: 700px;
}
/*/ Jump-to banner minimum rating, matches, retail price /*/
#control-options {
    color: #fff;
    height: 40%;
    font-size: 14px;
}
#matches {
    display: block;
    text-align: center;
    font-family: sans-serif !important;
    color: #fff;
    margin-bottom: 4px;
}
/*/ jump-to drop-down liste /*/
select#category-jumper {
    font-family: sans-serif !important;
    font: 11px sans-serif !important;
    color: #000000;
}
select#category-jumper {
    border-color: #fff;
    border-width: 1px;
    border: 3px solid #2b253a;
    border-radius: 5px;
    outline: none;
    -moz-appearance: none;
    -webkit-appearance: none;
    padding: 2px 8px;
}
/*/ Land type font /*/
body.search__results #search-results-wrap .search-section .search-section-title, body.search__permalink #search-results-wrap .search-section .search-section-title, body.search__commander #search-results-wrap .search-section .search-section-title {
    font-family: sans-serif !important;
    font-size: 32px;
    color: #efefef;
    font-weight: bold;
    text-align: center;
}
/*/ land tittel /*/
li.card-wrap .card-title {
    color: #efefef;
    font-family: sans-serif !important;
    text-align: center;
}
/*/ card hover tittel /*/
#hovercard #hovercard-title {
    position: absolute;
    bottom: 469px;
    bottom: 433px;
    vertical-align: baseline;
    left: 44px;
    width: 200px;
    font-size: 16px;
    font-family: sans-serif;
    text-align: center;
    background-color: #40355a;
    border: 0px solid transparent;
    z-index: 103;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
}
#hovercard #hovercard-title p a {
    color: #efefef;
}
#hovercard #hovercard-title p a:hover {
    color: #8fbe00;
    text-decoration: none;
}
/*/ card hover boks /*/
#hovercard #hovercard-box {
    position: absolute;
    bottom: 0;
    left: 10px;
    width: 268px;
    height: 446px;
    border: 0px solid transparent;
    -moz-border-radius: 7px;
    -webkit-border-radius: 7px;
    border-radius: 7px;
    background-color: gray;
    background-color: #40355a;
}
/*/ card hover lenker /*/
#hovercard #hovercard-box #hovercard-links #hovercard-affiliate-links #affiliate-channelfireball {
    color: #111;
}
#hovercard #hovercard-box #hovercard-links #hovercard-affiliate-links #affiliate-channelfireball {
    background-image: url(http://google.com) !important;
}
#hovercard #hovercard-box #hovercard-links #hovercard-affiliate-links #affiliate-channelfireball:hover {
    color: #111;
}


/*/ ABOUT, CONTACT, DONATE /*/
body.page #content-wrap #content, body.devise-registrations__edit #content-wrap #content {
    color: #fafafa;
    background-color: #5d994e;
    padding: 30px;
    font-family: sans-serif;
    font-size: 16px;
    border: 0px solid transparent;
    -moz-border-radius: 20px;
    -webkit-border-radius: 20px;
    border-radius: 20px;
    margin-bottom: 10px;
}
}
