}

piece.queen.black {
    /* Luna */
    background-image: url('https://cloud.full-stack.ninja/s/2gXyfAR7RTyz2DQ/download')!important;
}

piece.rook.black {
    /* Tank */
    background-image: url('https://cloud.full-stack.ninja/s/xNacAbT53Nwdz89/download')!important;
}

piece.knight.black {
    /* Trixie */
    background-image: url('https://cloud.full-stack.ninja/s/dMD3qaNXiyZMtGk/download')!important;
}

piece.bishop.black {
    /* Lightning Dust */
    background-image: url('https://cloud.full-stack.ninja/s/GRYwqSfnWGiD7Hx/download')!important;
}

piece.pawn.black {
    /* Changeling */
    background-image: url('https://cloud.full-stack.ninja/s/jKJ2nBSmTqRSQxS/download')!important;
}


/* White */
piece.king.white {
    /* Shining Armor */
    background-image: url('https://cloud.full-stack.ninja/s/4rr9K2Cn4PTqJWd/download')!important;
}

piece.queen.white {
    /* Celestia */
    background-image: url('https://cloud.full-stack.ninja/s/PBqGXbj2NWrLbfa/download')!important;
}

piece.rook.white {
    /* Owl */
    background-image: url('https://cloud.full-stack.ninja/s/k7zHKLiCwDKxFF2/download')!important;
}

piece.knight.white {
    /* Starlight Glimmer */
    background-image: url('https://cloud.full-stack.ninja/s/psGeRpgtMY9rMQf/download')!important;
}

piece.bishop.white {
    /* Rainbow Dash */
    background-image: url('https://cloud.full-stack.ninja/s/RFznALW2KtGETF4/download')!important;
}

piece.pawn.white {
    /* Derpy */
    background-image: url('https://cloud.full-stack.ninja/s/zKm7SbsC78GCAC3/download')!important;
}

body {
    /* Chess figures */
    background-image: url('https://cloud.full-stack.ninja/s/pa8Ze479FErKQN5/download')!important;

    background-size: cover;
    background-size: 60%;
    background-position: center;
    background-color: #112121;
    color: #D0D0D0;
}

#site_title {
    color: #CCC;
}

div.content_box {
    border: 1px solid #777;
    background: #344;
}

.button,
a.button {
    background: #566;
    color: #EEE;
}

.button:hover,
a.button:hover,
group.radio label:hover {
    border-color: #e2e2e2;
    background: #a8a8a8;
    background: linear-gradient(to bottom, rgba(148, 148, 148, 1) 0%, rgba(143, 143, 143, 1) 100%);
}

div.alternative a {
    color: #fff;
}

#topmenu section > a {
    color: #ccc;
    text-shadow: 0 1px 1px #000;
}

.crosstable th,
#friend_box .title,
div.side_box div.top,
div.box div.top,
.undertable_top,
.button,
a.button,
.button:visited,
group.radio label,
#notifications > div,
div.vstext,
div.user_show div.content_box_inter.tabs,
#qa .answers-header,
table.slist thead {
    background: linear-gradient(to bottom, rgba(145, 145, 145, 1) 0%, rgba(140, 140, 140, 1) 100%);
    text-shadow: 0 1px 0 #000;
    color: #EEE;
}

.brown .is2d .cg-board {
    /* Chess board */
    background-image: url('https://cloud.full-stack.ninja/s/WwZSPfBayxDij6o/download');
}

div.mchat .messages {
    background: rgba(1, 1, 5, 0.6);
}

div.mchat .messages span,
div.mchat .messages .user_link {
    color: #b0b0e0;
    margin-right: 2px;
}

/* move dest points */
.cg-board square.move-dest {
    background: radial-gradient(rgba(255, 255, 255, 0.3) 17%,#208530 0,rgba(0,0,0,0.3) 0,rgba(0,0,0,0) 0);
}


/* Title screen */

#hooks_wrap .table_wrap {
    text-shadow: 0 0px 0 #000;
    background: #222229;
}

table {
    background: #222229;
}

div.undertable_inner {
    background-color: #222229;
}

.scroll-shadow-hard {
    background: linear-gradient(#222229 30%,rgba(000,000,000,0)),linear-gradient(rgba(000,000,000,0),#222229 70%) 0 100%,radial-gradient(farthest-side at 50% 0,rgba(0,0,0,.2),rgba(0,0,0,0)),radial-gradient(farthest-side at 50% 100%,rgba(0,0,0,.2),rgba(0,0,0,0)) 0 100%;
}
}
