css:
@-moz-document url-prefix("https://uzlabina.cz"), url-prefix("https://www.uzlabina.cz"), url-prefix("http://lab.spse.uzlabina.cz/~stransky/"), url-prefix("http://lab.uzlabina.cz/~stransky/"), url-prefix("http://lab/~stransky/"), url-prefix("http://lab.uzlabina.lan/~stransky/"), url-prefix("https://skola.uzlabina.cz/==UCITELE_MATERIALY==/Stransky/"), url-prefix("https://propagace.uzlabina.cz/") {
/*
SPŠE Ječná: https://commons.wikimedia.org/wiki/File:Je%C4%8Dn%C3%A1_str,_Prague_New_Town.jpg
Photo by Packa
*/
body {
    background: #ff3232;
    background: -moz-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: -webkit-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: linear-gradient(to bottom, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: linear-gradient( rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)), url('https://upload.wikimedia.org/wikipedia/commons/thumb/8/85/Je%C4%8Dn%C3%A1_str%2C_Prague_New_Town.jpg/1200px-Je%C4%8Dn%C3%A1_str%2C_Prague_New_Town.jpg');
    font-family: CustomSans, "Comic Sans MS", "Comic Sans", cursive !important;
    text-align: center !important;
    text-shadow: 0.5px 0.5px 1px gray;
    font-style: italic;
    font-variant: small-caps;
    text-indent: 50px;
    letter-spacing: 0.3em;
    word-spacing: 0.5em;
    position: relative;
    top: 1em;
    transform: rotateY(15deg);
}

div.copyright > p:after{
    content: " Úžlabina Improved by Cvolton";
}

a[href="/vyhlaska50"]{
    font-size: 8em !important;
}

img[alt="logo praha"]{
    content:url("https://www.spsejecna.cz/ci/SPSE-Jecna_Logotyp2.png");
    width: 20em !important;
}

a[href="/vyhlaska50"] > img{
    width: 10em !important;
    height: 5em !important;
    
    content:url("https://i.imgur.com/mGasrIB.png");
}

img[alt="logo Microsoft Imagine"]{
    content:url("https://i.imgur.com/Kx46xqL.jpg");
}

img[alt="pečeť doporučení zaměstnavatelů"] {
    font-size: 20em;
}

img[alt="Elektrotechnika"]{
    width: 1em;
}

img[alt="Technické lyceum"]{
    width: 3em;
}

a[href="/elektrotechnika"] > p, a[href="/elektrotechnika"] > div.oborTitle > h3 {
    font-size: 0.5em !important;
}

a[href="/technicke-lyceum"] > p, a[href="/technicke-lyceum"] > div.oborTitle > h3 {
    font-size: 0.95em !important;
}

img[alt="Informační technologie"]{
    width: 25em;
}

a[href="/informacni-technologie"] > p, a[href="/informacni-technologie"] > div.oborTitle > h3 {
    font-size: 1.5em !important;
}

nav,
footer {
    background: #ff3232;
    background: -moz-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: -webkit-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: linear-gradient(to bottom, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    font-family: CustomSans, "Comic Sans MS", "Comic Sans" !important;
    font-weight: bold;
    font-style: italic;
    text-align: center !important;
}

.nav-custom > li > a {
    color: #0a0a0a !important;
    position: relative;
    top: +2px;
    border-bottom: 2px solid transparent;
    transition: all 300ms ease;
}

img {
    /*content:url(http://ecdl.org/build/img/logo-ecdl.png);
    ^ radsi nechat zakomentovane, jinak to zpusobi ecdl*/
    background-image: url("http://ecdl.org/build/img/logo-ecdl.png");
    background-size: contain;
}

h1 {
    background: #ff3232;
    background: -moz-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: -webkit-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    background: linear-gradient(to bottom, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #9198ff 68%, #f200ff 85%, #ff0004 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

p {
    background: #ff3232;
    background: -moz-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #f200ff 85%, #ff0004 100%);
    background: -webkit-linear-gradient(top, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #f200ff 85%, #ff0004 100%);
    background: linear-gradient(to bottom, #ff3232 0%, #f2ff00 17%, #00ff00 34%, #00fff6 51%, #f200ff 85%, #ff0004 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.container {
    color: #ff3232;
}

h2 {
    background: red;
    background: -webkit-linear-gradient(left, orange, yellow, cyan, blue, violet);
    background: -o-linear-gradient(right, orange, yellow, cyan, blue, violet);
    background: -moz-linear-gradient(right, orange, yellow, cyan, blue, violet);
    background: linear-gradient(to right, orange, yellow, cyan, blue, violet);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

h3 {
    background: red;
    background: -webkit-linear-gradient(left, orange, yellow, cyan, blue, violet);
    background: -o-linear-gradient(right, orange, yellow, cyan, blue, violet);
    background: -moz-linear-gradient(right, orange, yellow, cyan, blue, violet);
    background: linear-gradient(to right, orange, yellow, cyan, blue, violet);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
}

