css:
@-moz-document domain(poj.org) {


body{
	color: #000000;;
    background:url("/*[[bgp]]*/") no-repeat;
    background-size:cover;
	background-attachment:fixed;
	background-position:center top;
	background-color: /*[[bgcol]]*/;
}
tbody{
	background-color: /*[[bgcol]]*/;
}
table{
	margin:10px 15% 0px 15%;
	width: 70%;
	background-color: /*[[bgcol]]*/;
 	opacity:/*[[opa]]*/;
}
table.h1{
	margin:1px 0px 0px 0px;
	width: 100%;
	opacity:/*[[opa]]*/;
}
table.h2{
	margin:1px 0px 0px 0px;
	width: 100%;
 	opacity:/*[[opa]]*/;
}
td{
	width: 1px;
}
p{
	background-color: /*[[bgcol]]*/;
	margin:10px 15% 0px 15%;
 	opacity:/*[[opa]]*/;
	border-radius: 10px;
}
p.pst{
	background-color: /*[[bgcol]]*/;
	margin:20px 4% 20px 4%;
 	opacity:/*[[opa]]*/;
}
div.ptx{
	background-color: /*[[bgcol]]*/;
	margin:20px 4% 20px 4%;
 	opacity:/*[[opa]]*/;
}
pre.sio{
	background-color: /*[[bgcol]]*/;
	margin:20px 4% 20px 4%;
	opacity:/*[[opa]]*/;
}

}
