css:
@-moz-document domain("1993s.top"), domain("zxzjs.com"), domain("zxzj.me") {
body {
	background: url(http://wx4.sinaimg.cn/large/6788d438ly1g58k2knx8pj21hc0xcn03.jpg)no-repeat;
	background-size: cover;
	background-attachment: fixed;

	color: #f2f2f9!important;
}


DIV.head.clearfix:first-child > SPAN,
DIV.head.clearfix:first-child > A.open-screen.pull-right:first-child {
	color: #000
}



DIV.stui-vodlist__head:nth-child(n) > H3:last-child,
DIV.stui-vodlist__head:nth-child(8) > H3 > A,
UL.stui-header__menu:last-child > LI.active:nth-child(n) > A,
P:nth-child(n) > A:nth-child(n),
A.detail-more:last-child,
DIV.stui-content__detail:nth-child(2) > H1.title:first-child,
UL.tab.clearfix:first-child > LI.active:nth-child(n) > A,
UL.clearfix:nth-child(n) > LI:nth-child(n) > A,
DIV.stui-vodlist__head:nth-child(n) > H3:nth-child(2) > A,
DIV.stui-vodlist__head:nth-child(n) > A {
	color: #73c8fbf5
}


a:hover{
	color: #73c8fbf5
}

【集数-颜色】,
DIV.item.active:first-child > DIV.play-content:last-child > DIV.play-item.cont.active:last-child > UL.stui-play__list.clearfix > LI.active:nth-child(n) > A,
DIV.item.active:first-child > DIV.play-content:last-child > DIV.play-item.cont.active:nth-child(2) > UL.stui-play__list.clearfix > LI.active:nth-child(n)> A,
 DIV.item.active:first-child > DIV.play-content:last-child > DIV.play-item.cont.active:first-child > UL.stui-play__list.clearfix > LI.active:nth-child(n) > A{
	background: #323232;	color: #73c8fbf5
}

UL.stui-gbook-form > LI:last-child > BUTTON.submit.gbook_submit.pull-right:first-child{
	background: #7ebeff;
}

DIV.stui-content__detail:nth-child(2) > DIV.play-btn:last-child > A{
	background: #717171;
}
.stui-header__menu li.active a:before,
.stui-player__side .tab li.active a:before {
    background-color: #7ebeff;
}


UL.stui_header__user:first-child > LI:nth-child(n) > A,
UL.stui_header__user:first-child > LI.date:last-child > SPAN,
UL.stui-content__playlist.clearfix:nth-child(n) > LI:nth-child(n) > A,

DIV.stui-player__detail:last-child > H1.title:nth-child(2),
DIV.data:nth-child(3) > A.open-desc,

UL.stui-gbook__item.clearfix:nth-child(n) > LI.name:first-child > H4:last-child ,
I.iconfont.icon-back,
I.iconfont.icon-more{
	color: #66ffa1e3
}

UL.stui-play__list.clearfix > LI:nth-child(n) > A {
	background: #4d504ec7;
	color: #66ffa1e3
}

LI.index:first-child > DIV.stui-vodlist__detail.index:last-child {
	background: #8bec95d9;
	color: #3e403ee3;
}

DIV.stui-vodlist__head:nth-child(6) > H3 > A,
UL.stui-header__menu:last-child > LI:nth-child(n) > A,
DIV.stui-player__detail:last-child > DIV.data:nth-child(3),
.stui-vodlist__detail {
	color: #e59e9e
}

DIV.stui-vodlist__box > DIV.stui-vodlist__detail:last-child,
DIV.stui-screen:first-child > DIV.head.clearfix:first-child {
	background: #e59e9e
}

DIV.container:first-child > DIV.row > DIV.stui-pannel > DIV.stui-pannel__hd:first-child > HEADER.stui-header.clearfix:first-child > DIV.container > DIV.row > UL.stui_header__user:first-child > LI:nth-child(2) > UL.dropdown.type.clearfix:last-child > LI.active:nth-child(2) > A {
	background: #66ffa1e3;
	color: #282828
}



#screenbox > UL.clearfix,
#stui_history > LI.top-line:nth-child(n) > A,
UL.stui_header__user:first-child > LI:first-child > DIV.dropdown.search:last-child,
UL.stui_header__user:first-child > LI:nth-child(2) > UL.dropdown.type.clearfix:last-child,
UL.dropdown.type.clearfix:last-child > LI:nth-child(n) > A,
UL.stui_header__user:first-child > LI:nth-child(3) > DIV.dropdown.history:last-child,
DIV.head.clearfix:first-child > UL.tab-top.play-tab.clearfix:nth-child(2),
UL.tab-top.play-tab.clearfix:nth-child(n) > LI:nth-child(n) {
	border-color: #e59e9e;
	background: #454444;
}

UL.stui_header__user:first-child > LI:first-child > DIV.dropdown.search:last-child,
UL.stui_header__user:first-child > LI:nth-child(2) > UL.dropdown.type.clearfix:last-child,
UL.dropdown.type.clearfix:last-child > LI:nth-child(n) > A,
UL.stui_header__user:first-child > LI:nth-child(3) > DIV.dropdown.history:last-child,
DIV.head.clearfix:first-child > UL.tab-top.play-tab.clearfix:nth-child(2) {
	Border: 1px solid #66ffa1e3;
}

DIV.container:first-child > DIV.row > DIV.stui-pannel > DIV.stui-pannel__hd:first-child > HEADER.stui-header.clearfix:first-child > DIV.container > DIV.row > UL.stui_header__user:first-child > LI:nth-child(2) > UL.dropdown.type.clearfix:last-child > LI.active:nth-child(n) > A {
    background: #66ffa1e3;
	color: #282828;}

UL.dropdown.type.clearfix:last-child > LI:nth-child(n) > A {
	Border: #cfbfbf00
}

.row .stui-pannel,
.stui-pannel__bd,
.stui-player.clearfix,
stui-content:first-child > DIV.stui-content__detail:nth-child(2),
.stui-content__side:last-child,
.stui-content:first-child > DIV.stui-content__detail:nth-child(2),
.item.active:first-child > DIV.head.clearfix:first-child {
	background: #fffafa00;
}






.stui-content:first-child,
UL.stui-content__playlist.clearfix:nth-child(n) > LI > A {
	background: #403d3d;
}

UL.stui-content__playlist.clearfix:nth-child(n) > LI > A,
DIV.item.active:first-child > DIV.head.clearfix:first-child > H3:last-child,
#stui_history > LI.top-line:nth-child(n) > A > SPAN.pull-right.text-red,
DIV.container:first-child > DIV.row > HEADER.stui-header.clearfix:first-child > DIV.container > DIV.row > UL.stui_header__user:first-child > LI:nth-child(n) > UL.dropdown.type.clearfix:last-child > LI:nth-child(n) > A {
	color: #dbe0e3;
}

DIV.container:first-child > DIV.row > DIV.stui-pannel > DIV.stui-pannel__ft:last-child > UL.stui-page__item.text-center.clearfix > LI.hidden-xs:nth-child(n) > A,
DIV.container:first-child > DIV.row > DIV.stui-pannel > DIV.stui-pannel__ft:last-child > UL.stui-page__item.text-center.clearfix > LI:nth-child(n) > A {
	background: #4b4646c2;
	color: #66ffa1e3
}

DIV.container:first-child > DIV.row > DIV.stui-pannel > DIV.stui-pannel__ft:last-child > UL.stui-page__item.text-center.clearfix > LI.hidden-xs.active:nth-child(n) > A {
	background: #66ffa1e3;
	color: #141414;
}



UL.stui-page__item.text-center.clearfix > LI:first-child > A,
UL.stui-page__item.text-center.clearfix > LI:last-child > A,
UL.stui-page__item.text-center.clearfix > LI:nth-child(2) > A,
UL.stui-page__item.text-center.clearfix > LI:nth-child(9) > A {
	background: #2d2c2c00;
}

UL.stui-page__item.text-center.clearfix > LI.hidden-xs:nth-child(n) > A {
	color: #343433;
}

DIV.stui-player.clearfix > DIV.stui-player__side:last-child,
.stui-player__detail {
	background: #2d2c2c00;
}



UL.tab.clearfix:first-child > LI:first-child > A,
UL.tab.clearfix:first-child > LI:last-child > A,
DIV.head.clearfix:first-child > H3:last-child > A,
DIV.head.clearfix:first-child > H3:last-child > A,
DIV.head.clearfix:first-child > A.play-switch.pull-right:first-child,
UL.clearfix:nth-child(n) > LI:first-child > SPAN {
	color: #fffafa
}



LI:first-child > TEXTAREA.form-control,
INPUT.form-control:nth-child(2) {
	background: #f1f1f1f2;
	color: #fffafa
}
}
