css:
@-moz-document url-prefix("https://pawoo.net/web/"), url-prefix("https://pawoo.net/@"), url-prefix("https://pawoo.net/users/") {
/* general background */
body, .app-holder {
    background: #f1f1f1 !important;
}

/* navigation button */
.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>*{
    background: #b9b9b9;
}

.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>*:hover{
    background: #777;
}

/* subcolumn */
.pawoo-subcolumn__header {
    background: #fff;
}

.trend-tags {
    background: transparent;/* remove subcolumn black line*/
}

/* scrollbar */
::-webkit-scrollbar-thumb {
    background: #999 !important;
}

::-webkit-scrollbar-thumb:hover, -webkit-scrollbar-thumb:active {
    background: #777 !important;
    border: none;
}

::-webkit-scrollbar-track, -webkit-scrollbar-track:hover {
    background: #ccc !important;
    border-radius: 0 0 4px 0;
    border: none;
}

/* account authorize */
.account-authorize__wrapper {
    background: #fff;
}

.display-name{
    color: #000;
}

.account__header__content{
    color: #222;
}

.account--panel {
    background: #b9b9b9;
    border-color: transparent;
}

.icon-button {
    color: #666;
}
.icon-button:hover {
    color: #333;
}

/* user info */
.card .card__bio {
    background: rgba(23, 25, 31, 0.2);
}

.account__header__content {
    color: #eee;
}

#pawoo-suggestion-strip .pawoo-subcolumn__header {
    background: #ccc;
    color: #444;
}

.pawoo-subcolumn--suggested-accounts__body, #pawoo-suggestion-strip .suggestion-tags__body {
    background: #fff;
    color: #444;
}

.suggestion-tags__name a{
    color: #333;
}
.suggestion-tags__name a:hover {
    color: #000;
}

.suggestion-tags__description {
    color: #444;
}

.suggestion-tags__description.suggestion:before {
    background-image: url(data:image/png;base64,);
    opacity: 1;
}

/* for Pawoo+ */
.detailed-comments-container a, .detailed-comments-container span{
    color: #444 !important;
}
.reply_inline_comment_textarea {
    color: #444 !important;
}
}
