css:
@-moz-document domain("bloxtopia.xyz") {
body
{
	font: normal 8pt/normal 'Verdana' , Verdana, sans-serif;
	margin-top: 0;
	text-transform: none;
	text-decoration: none;
	background:#F8FCFF url(http://web.archive.org/web/20100417023823im_/http://www.roblox.com/images/bg_gradient.jpg) repeat-x;
}

#Banner
{
	background-image: url(https://web.archive.org/web/20100417023823im_/http://www.roblox.com/images/WoodBanner.png);
	background-repeat: no-repeat;
	border-bottom: solid 1px #6F99CB;
	border-left: solid 1px #6F99CB;
	border-right: solid 1px #6F99CB;
	height: 72px;
	text-align: center;
}

#Banner #Logo
{
	float: left;
	margin: 0;
	text-align: center;
	width: 293px;
  opacity: 0;
}

#Header .Navigation
{
	clear: both;
	background-color: #6e99c9;
	border-bottom: solid 1px #6F99CB;
	border-left: solid 1px #6F99CB;
	border-right: solid 1px #6F99CB;
	color: #fff;
	font-family: 'Verdana' , Verdana, sans-serif;
	font-size: 12pt;
	font-weight: normal;
	line-height: 2em;
	text-decoration: none;
}

#Container {
 margin:0 auto;
 padding:0 10px;
 border-left:5px solid #DDD;
 border-right:5px solid #DDD;
 width:900px;
 background:#e5f1fd;
}


#LoginView {
 border:solid 2px #6F99CB;
 background-color: #fff
}

#LoginView h5 {
 background-color:#6F99CB;
 color: #fff;
 border-bottom:solid 2px #6F99CB;
 margin:0;
}

#SplashContainer #Figure
{
	margin-top: 60px;
	opacity:0
}

#SplashContainer #Figure
{
	margin-top: 60px;
	opacity:0
}

#SplashContainer #RobloxAtAGlance
{
	background-color: #fff;
	border: solid 2px #6F99CB;
	float: right;
	margin: 0 0 10px 0;
	padding: 10px;
	position: relative;
	text-align: left;
	width: 715px;
}

#SplashContainer #UserPlacesPane
{
	border: solid 2px #6F99CB;
	clear: both;
	margin: 0;
	background-color: #fff
}

#SplashContainer #UserPlacesPane #UserPlaces_Header
{
	background-color: #6F99CB;
	border-right: solid 2px #6F99CB;
  color: #fff;
	padding: 10px;
	width: 142px;
}

#SplashContainer #UserPlacesPane #UserPlaces_Header h3
{
	color: #fff;
}

#FriendsPane
{
	clear: right;
	margin: 10px 0 0 0;
  background-color: #fff;
}

}
