css:
@-moz-document domain("community.monogame.net") {
:root
{
		/*User colors*/
		--main-colour: #EF4C3C;
    	--light-colour: #ffa29a;
        --dark-colour: #221b1b;
		--main-background: #151515;
		--second-background: #222;
		--hover-background: #333;
		--main-text: #babfc4;
		--dimmer-text: #959595;
        --border-colour: #0005;
}

/*Home*/
.site-navigation {
    background: var(--second-background);
}
.d-header {
    background: var(--second-background);
}

#menu-main li a {
    color: var(--main-text);
}
#main {
    background: var(--main-background);
}

.list-controls .category-breadcrumb li.bullet > .badge-category {
    background: var(--main-background) !important;
    color: var(--main-text) !important;
}
.list-controls .category-dropdown-menu {
    border: 1px solid var(--border-colour);
    background-color: var(--main-background);
}
.list-controls .category-dropdown-menu .home {
    color: var(--dimmer-text);
}

.topic-list a.title {
    color: var(--main-colour);
}
.nav-pills > li > a {
    color: var(--main-colour);
}

.btn {
    color: var(--main-colour);
    background-color: var(--main-background);
}

.topic-list .topic-excerpt {
    color: var(--main-text);
}

.badge-wrapper.bullet span.badge-category {
    color: var(--light-colour) !important;
}

.topic-list th {
    color: var(--main-text);
}

.topic-list .num a:not(.badge-posts) {
    color: var(--main-text);
}

.topic-list td {
    color: var(--main-text);
}

.badge-posts[href] {
    color: var(--main-text);
}

.menu-panel {
    border: 1px solid var(--border-colour);
    background-color: var(--second-background) !important;
}
.panel-body-contents .search-context label {
    color: var(--main-text);
}
.search-menu .search-context .show-help {
    color: var(--main-colour);
}
.drop-down-visible .d-header .icons .active .icon {
    background-color: var(--second-background);
    border-top: 1px solid var(--border-colour);
    border-left: 1px solid var(--border-colour);
    border-right: 1px solid var(--border-colour);
}





/*Forum*/
#topic-title h1 a {
    color: var(--main-colour);
}

a {
    color: var(--light-colour);
}

.names span a {
    color: var(--main-colour);
}

.cooked, .d-editor-preview {
    color: var(--main-text);
}

.topic-map {
    background: var(--second-background);
    border: 1px solid var(--border-colour);
}
.topic-map section {
    border-top: 1px solid var(--border-colour);
}
.topic-map .map .number, .topic-map .map i {
    color: var(--main-text);
}
.topic-map h3 {
    color: var(--main-text);
}
.topic-map .buttons .btn {
    color: var(--main-text);
    background: var(--hover-background);
    border-left: 1px solid var(--border-colour);
    border-top: 1px solid var(--border-colour);
}

a.mention, a.mention-group {
    color: var(--main-colour);
    background: var(--hover-background);
}

nav.post-controls button.create {
    color: var(--main-colour);
}

#topic-footer-buttons p {
    color: var(--main-text);
}

body h3 {
    color: var(--main-colour);
}

.btn[disabled], .btn.disabled {
    background: var(--second-background);
}
#topic-progress-expanded {
    border: 1px solid var(--border-colour);
    background: var(--second-background);
}
body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
    background: var(--second-background);
    border: 1px solid var(--border-colour);
    color: var(--main-text);
}
#topic-progress {
    color: var(--main-colour);
    border: 1px solid var(--border-colour);
    background: var(--second-background);
}

#reply-control {
    background-color: var(--second-background);
}
body .grippie {
    border-top: 1px solid var(--border-colour);
    background: var(--second-background);
}

#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview {
    background-color: var(--main-background);
}
#reply-control .contents .d-editor-preview {
    border: 1px solid var(--border-colour);
}
#reply-control .d-editor-button-bar {
    color: var(--main-text);
    background-color: var(--main-background) !important;
}
.d-editor-preview > :first-child {
    color: var(--main-text);
}
textarea#ember1667.ember-view ember-text-area d-editor-input {
    background: var(--main-background) !important;
}

.bootbox.modal {
    background-color: var(--second-background) !important;
    border: 1px solid var(--border-colour);
}
.modal-footer {
    border-top: 1px solid var(--border-colour);
}
.modal-body {
    color: var(--main-text);
}
.modal-footer .btn + .btn {
    color: var(--main-text);
}

/*Code Box*/
pre code {
    color: var(--main-colour);
    background: var(--second-background);
}
.hljs-title, .hljs-name, .coffeescript .hljs-params, .scss .hljs-meta {
    color: #cf3636;
}
.hljs-tag, .hljs-tag .hljs-title, .django .hljs-tag .hljs-keyword {
    color: #5a94c6;
}
.hljs-string, .hljs-tag .hljs-string, .tex .hljs-formula {
    color: #d74d6f;
}
.hljs {
    color: var(--main-text);
}
.hljs-keyword, .javascript .hljs-title, .nginx .hljs-title, .hljs-subst, .hljs-request, .hljs-status {
    color: #536fdb;
}
.hljs-built_in, .lisp .hljs-title, .clojure .hljs-built_in, .hljs-builtin-name {
    color: #49bb52;
}

/*Quote*/
blockquote {
    border-left: 5px solid var(--border-colour);
    background-color: var(--second-background) !important;
}
}
