css:
@-moz-document domain("mstdn.io"), domain("mastodon.social") {
body {
    font-family: "Noto Sans";
}

body.app-body,
.ui,
.drawer__inner,
.drawer__inner__mastodon {
    background-color: black;
}

.column {
    min-width: 330px;
    width: 20vw;
}

.navigation-bar,
.compose-form {
    padding: 10px 0;
}

.navigation-bar .navigation-bar__actions .compose__action-bar .icon-button {
    margin-right: -12px;
}

.column > .scrollable {
    background-color: #121212;
}

.focusable:focus,
.detailed-status,
.detailed-status__action-bar {
    background-color: #1a1a1a !important;
}

.account__header > div {
    background: linear-gradient(transparent, #0006);
    text-shadow: black 0 0 1px, black 0 0 2px, black 0 0 8px, #000000e6 0 0 8px;
}

.account__header .emojione {
    filter: drop-shadow(black 0 0 1px);
}

.account__header a,
.reply-indicator__content a,
.status__content a {
    color: #8ddfff;
    text-decoration: none;
}

.account__header a:hover,
.reply-indicator__content a:hover,
.status__content a:hover {
    text-decoration: underline;
}

.account__header .account__header__content,
.account__header .account__header__display-name,
.account__header .account__header__username {
    color: white;
    margin: -10px;
    padding: 10px;
    font-weight: 500;
}

.account__header__avatar {
    box-shadow: black 0 0 2px;
}

.account__header .account--action-button .icon-button {
    color: white !important;
    text-shadow: black 0 0 2px;
}

.account__header .account__header__username {
    font-weight: bold;
}

.account__header .account__header__fields {
    margin-top: 0;
    padding: 0 10px;
}

.account__header .account__header__fields dt,
.account__header .account__header__fields dd {
    background: none;
    color: white;
}

.account__header .account__header__fields dd.verified {
    background: none;
    border: none;
}

.account__header__fields,
.account__header__fields dl,
.account__header .account__header__fields dl {
    border: none;
}

.account__header .account__header__fields dt {
    text-align: left;
}

.account__header .account__header__fields dd {
    text-align: right;
}

.account__section-headline a.active::after {
    border-color: transparent transparent #121212;
}
}
