css:
@-moz-document url-prefix("https://greasyfork.org/") {
body
{
  background-color: #f7f9f8 !important;
}

#main-header,
body:not(.Settings) #Head
{
  background-color: #90b5d5 !important;
  background-image: linear-gradient(#4f80b1, #f7f9f8) !important;
  box-shadow: none !important;
}

a
{
  color: #0089bf !important;
}

a:visited
{
  color: #004a67 !important;
}

#main-header,
#main-header a,
#main-header a:visited,
#main-header a:active,
#nav-user-info a
{
  color: white !important;
}

#main-header h1 a,
#main-header h1,
#site-name-text h1,
#site-name-text h1 a
{
  color: #fff !important;
  text-shadow: 5px 5px 5px #50759580 !important;
}

#main-header #site-nav nav a,
.SiteMenu a
{
  color: #000 !important;
}

pre,
code,
#code-container,
.Message pre ol
{
  border: #b6bcc1 !important;
  background-color: #f5fbff !important;
}

.code-container
{
  border: 1px solid #c4cace !important;
}

/* code background */
.linenums
{
  background-color: #ebf3f9 !important;
}

/* zebra code lines */
li.L1,
li.L3,
li.L5,
li.L7,
li.L9
{
  background-color: inherit !important;
}

.kwd
{
  color: #0d3e9f !important;
  font-weight: bold !important;
}

.str
{
  color: #1c8606 !important;
}

.com
{
  color: #800 !important;
  font-style: italic !important;
}

.typ
{
  color: #8e338e !important;
}

.lit
{
  color: #b67d16;
}

.clo,
.opn,
.pun
{
  color: #004e66;
}

/* line number vertical border */
.linenums > li:before
{
  content: '' !important;
  border-left: 1px solid #f0f7fd !important;
  padding-left: 1em !important;
}

/* line numbers */
.linenums > li::marker
{
  color: #bec4c8 !important;
}

.list-option-group .list-current
{
  border-left: 7px solid #006288 !important;
}

nav nav,
.SiteMenu nav
{
  background-color: #abc6de !important;
}

.pagination > *,
.script-list + .pagination > *,
.user-list + .pagination > *,
.Pager > *,
.RecentDiscussionFilter a
{
  background-color: #e5edf2 !important;
}

.pagination > a:hover,
.pagination > a:focus,
.Pager > a:hover,
.Pager > a:focus,
.RecentDiscussionFilter a:hover,
.RecentDiscussionFilter a:focus
{
  background-color: #c5d8e2 !important;
}

.pagination .current,
.pagination .gap,
.Pager > .Ellipsis,
.Pager > .Highlight
{
  background-color: transparent !important;
}

.install-link,
.install-link:visited,
.install-link:active,
.install-link:hover,
.install-help-link
{
  background-color: #81a6c5 !important;
  color: white !important;
}

.install-help-link,
.install-help-link:visited,
.install-help-link:active,
.install-help-link:hover
{
  background-color: #91b6d5 !important;
  color: #fff !important;
}

.list-option-group .list-current,
.tabs .current,
#Panel .FilterMenu .Active a,
#Panel .PanelCategories .Active a,
#Content .HomepageTitle
{
  border-color: #7c99b1 !important;
}

form.new_user input[type="submit"]
{
  background-color: #90b5d5 !important;
  background-image: linear-gradient(#bbdaf2, #4b7baa) !important;
}

#main-header .subtitle
{
  text-shadow: -1px -1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, 1px 1px 0px #000;
}

.user-content
{
  background: linear-gradient(90deg, #ecf7fb, #fff 1em);
  border-left: 2px solid #ecf7fb;
}

.user-screenshots img
{
  padding-bottom: 20px !important;
}

}
