css:
@-moz-document url-prefix("https://greasyfork.org/") {
body
{
  background-color: #f7f9f8 !important;
}

#main-header,
body:not(.Settings) #Head
{
  background-color: #90b5d5 !important;
  background-image: linear-gradient(#4f80b1, #f7f9f8) !important;
  box-shadow: none !important;
}

a
{
  color: #0089bf !important;
}

a:visited
{
  color: #004a67 !important;
}

#main-header,
#main-header a,
#main-header a:visited,
#main-header a:active,
#nav-user-info a
{
  color: white !important;
}

#main-header h1 a,
#main-header h1,
#site-name-text h1,
#site-name-text h1 a
{
  color: #fff !important;
  text-shadow: 5px 5px 5px #50759580 !important;
}

#main-header #site-nav nav a,
.SiteMenu a
{
  color: #000 !important;
}

#additional-info > div
{
  border: 1px solid #dae5ea !important;
  background-color: #e5eef2 !important;
}

pre,
code,
#code-container,
.Message pre ol
{
  border: #c9d9e3 !important;
  background-color: #f5fbff !important;
}

.CodeRay .line-numbers,
.linenums
{
  background-color: #e4ebef !important;
}

.list-option-group .list-current
{
  border-left: 7px solid #006288 !important;
}

nav nav,
.SiteMenu nav
{
  background-color: #abc6de !important;
}

.pagination > *,
.script-list + .pagination > *,
.user-list + .pagination > *,
.Pager > *,
.RecentDiscussionFilter a
{
  background-color: #e5edf2 !important;
}

.pagination > a:hover,
.pagination > a:focus,
.Pager > a:hover,
.Pager > a:focus,
.RecentDiscussionFilter a:hover,
.RecentDiscussionFilter a:focus
{
  background-color: #c5d8e2 !important;
}

.pagination .current,
.pagination .gap,
.Pager > .Ellipsis,
.Pager > .Highlight
{
  background-color: transparent !important;
}

.install-link,
.install-link:visited,
.install-link:active,
.install-link:hover,
.install-help-link
{
  background-color: #81a6c5 !important;
  color: white !important;
}

.install-help-link,
.install-help-link:visited,
.install-help-link:active,
.install-help-link:hover
{
  background-color: #91b6d5 !important;
  color: #fff !important;
}

.list-option-group .list-current,
.tabs .current,
#Panel .FilterMenu .Active a,
#Panel .PanelCategories .Active a,
#Content .HomepageTitle
{
  border-color: #7c99b1 !important;
}

.CodeRay .comment
{
  color: #b6bfc5 !important;
}

.CodeRay .keyword
{
  color: #508fc6 !important;
}

.CodeRay .string,
.CodeRay .string > *
{
  color: #88b724 !important;
  background-color: transparent !important;
}

.CodeRay .integer
{
  color: #ba6300 !important;
}

.CodeRay .key
{
  color: #bc7ecc !important;
}

.CodeRay .function
{
  color: #31b8c1 !important;
}

.CodeRay .local-variable
{
  color: #009f90 !important;
}

.CodeRay .regexp .content
{
  color: #64a5d0 !important;
}

.CodeRay .regexp .modifier
{
  color: #a78bea !important;
}

.CodeRay .regexp
{
  background-color: rgba(55, 138, 170, 0.05) !important;
}

form.new_user input[type="submit"]
{
  background-color: #90b5d5 !important;
  background-image: linear-gradient(#bbdaf2, #4b7baa) !important;
}

#main-header .subtitle {
    text-shadow: -1px -1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, 1px 1px 0px #000;
}
}
