css:
@-moz-document domain("scpfoundation.ru"), domain("scpnet.org"), domain("scp-wiki.net"), domain("scpfoundation.net") {
/* Шапка */
div#container-wrap {
    background: url(http://scp-ru.wdfiles.com/local--files/component%3Atheme/header_dark.png) top left repeat-x;
    }

/* Тело */
#page-title, body {
    background: #191919 url("data:image/png;base64,") repeat !important; }
input[type="button"], input[type="submit"] {
  line-height: 19px;
    color: #000000 !important;
}

.interwiki,  #side-bar .side-block, div.scp-image-block, .rimg, .page-rate-widget-box, #search-top-box-form .search-query, .btn-default, #page-content .rate-points, #page-content, .scp-featured__block, .scp-other-branches, #page-content blockquote, #toc, .scp-image-caption, .description-block, .cimg, .content-panel, .form{
    background-color: #19191996 !important;
    border: 1px solid #151515 !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #808080 !important;
}

[id="recent-posts-category"] {
    background: #373636 !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #b0abab !important;
    border: 1px solid #151515 !important;
}

[style="text-align: center; padding: 2px 20px; color: black; font-size: 8pt; background-color: #fff0f0; font-family: Verdana, Arial, sans-serif; border-radius:30px /70px;"] {
background-color: #191919 !important;
    border: 1px solid #151515 !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #b0abab !important;
}
[style="color: #f5e9e9; font-size: 8pt; background-color: #009494; font-family: Verdana, Arial, sans-serif; border-radius:30px /70px; background:#009494; background:linear-gradient(#00cccc, #009494); text-align: left; padding: 2px 20px;"] {
    background: #373636 !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #b0abab !important;
}

[style="color: #f5e9e9; font-size: 8pt; background-color: #009494; font-family: Verdana, Arial, sans-serif; border-radius:30px /70px; background:#009494; background:linear-gradient(#00cccc, #009494); text-align: left; padding: 2px 20px;"], [style="color: gold; font-size: 10pt; background-color: #4E5057; background:#4E5057; text-align: left; padding: 10px 20px;"], [style="color: white; font-size: 8pt; border-radius:30px /70px; background:#6c316c; background:linear-gradient(#562756, #6c316c); text-align: left; padding: 2px 20px;"] {
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #b0abab !important;}

[style="text-align: center;"] {color: #808080 !important;}
input[type="button"], input[type="submit"]{
    background-color: #19191996 !important;
    border: 1px solid #151515 !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #f8919196 !important;
    background: linear-gradient(to bottom, #473e3e96,#544f4f96,#19191996) !important;
}
.thread-container .post .head, .long {
    background: #151515;
    border: solid 2px #151515 !important;;
}
#top-bar li a {
    background-color: #19191996 !important;
    border: 1px solid #151515 !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5) !important;
    color: #f8919196 !important;
    background: #191919 !important;
}

#page-content, .scp-featured__page-title a, .scp-featured__previous a{
    border: solid 5px #19191996 !important;
    color: #808080
}

[href="javascript:;"],[class="sfhover"] { background-color: #19191996 !important;
    color: #f8919196 !important;}

#side-bar a, .interwiki a, div{
    color: #aa5252 !important;
}
.heading, .interwiki__title {
    color: #bd1b1b !important;
}
#top-bar a, .rimg , div.side-block {
  color: #bbb !important;
  background: transparent;
}


h4, .scp-other-branches a{
    color: #b2b2b2;
    font-weight: bold;
}

h1{
    color: #ffa1a1;
    font-weight: bold;
}

h2{
    color: #e16363;
    font-weight: bold;
}

h3{
    color: #9b4444;
    font-weight: bold;
}

[href="/safe"], [href="/euclid"], [href="/keter"], [href="/thaumiel"] {text-shadow:1px 1px #BBB}
[href="/safe"] {color:#090; text-shadow: none}
[href="/euclid"] {color:#FE0; text-shadow: none }
[href="/keter"] {color:#F00; text-shadow: none }
[href="/thaumiel"] {color:#000; text-shadow: none}
#toc-action-bar > a:nth-child(1)::after {content: " (скрыть)"}
#toc-action-bar > a:nth-child(2)::after {content: " (показать)"}
#toc .title {display: none} #toc-list::before {font-weight: bold; padding: 0 1em; content: "Содержание"}
.footnotes-footer .title {display: none} #footnote-1::before {font-size: 130%; font-weight: bold; display:table-header-group; content: "Примечания"}

/*.wiki-content-table, .pager {
    color: #0d0000 !important;
  background: #19191996;
}*/
.yui-navset, .yui-navset-top div, th {color: #b2b2b2 !important;
  background: #19191996!important;}

:link {
    color: #808080;
    font-weight: bold;
}

img[alt="thaumiel.png"] {
    content:url(http://scp-ru.wdfiles.com/local--files/component%3Atheme/thaumiel.png);
}

a:visited {
        color: #808080;
        font-weight: bold;
}
}
