css:
@-moz-document domain(politico.eu) {
:root {
    --color-font: #ddd;
    --color-font2: #bbb;
    --color-borders: #666;
    --color-link: #9cc;
}

.global-header,
.nav-macro > h5,
.nav-macro > h5:hover,
.micro-list > li > a,
.micro-list > li > a:hover,
.subnav.is-active .subnav-content{
    background: #111 !important;
}

body,
.super,
.super.contrast-high,
.user-tools.sticky-content.is-stuck,
.ir-2019-european-elections-2019-european-elections .installment-report-header, 
.single .installment-report-header,
.installment-report,
.ir-2019-european-elections-2019-european-elections .feature-grid article{
    background: #222 !important;
    color: var(--color-font) !important;
    border-color: var(--color-borders) !important;
}

.widget.caste-exclusive,
.widget.caste-exclusive .widget-header, 
.widget.caste-exclusive .widget-content, 
.widget.caste-exclusive .widget-footer,
input, select, textarea,
input:focus,
.story-frag.sponsored-wrapper{
    background: #333 !important;
    border-color: var(--color-borders) !important;
}

ul.headline-list li a,
.story-frag header h3 a,
.widget-header h2 a,
.generic-carousel-group.slick-slider .niche-frag a,
input, select, textarea,
input:focus,
.installment-report .feature-grid .feature-text-below h4, 
.installment-report .feature-grid .feature-text-below h5, 
.installment-report .feature-grid .feature-text-below cite,
.textwidget a,
.segment-summary header h2 a,
.story-intro .summary .subhead{
    color: var(--color-font) !important;
}

.story-frag p,
.story-frag .meta a,
.story-frag .meta a:hover,
.story-frag .related-list a,
.widget-footer li a,
.widget-footer li a:hover,
.magazine figcaption.story-intro p,
.utility-email a,
.wp-caption p.wp-caption-text,
.installment-report .feature-grid article a,
.segment-summary header p,
.sd-sharing li,
.story-intro .meta p,
.url.fn{
    color: var(--color-font2) !important;
    border-color: var(--color-borders) !important;
}

.global-header,
.story-frag-list.layout-linear > li + li,
.cluster-groupset.layout-grid .cluster-group::before,
.vertical-group > h6,
ul.article-in-this-section,
.content-groupset + .content-groupset,
.tools-group,
.super + .super::before, 
.super.contrast-high:first-child::before, 
.super.contrast-medium:first-child::before,
.front-list > li + li{
    border-color: var(--color-borders) !important;
}

.micro-list > li > a,
.nav-macro > h5,
[id="pro-menu"]{
    border:none !important;
}

.story-text a {
    color: var(--color-link) !important;
}

.story-text .category a {
    color: #b00 !important;
}
}
