css:
/* i really want this to be global */
/*General info about the theme:*/
/*
The nice blue for the theme: #2196f3
The nice grey for the theme: #282828


*/


/*background*/
	div.top-content.app-background.xli-flex-enabled {
		background: url("https://i.imgur.com/EBs5NIr.png");
		background-size: 100% 100%;
		min-height: 100%;
	}


	div.mat-card.mat-card {
		background: -webkit-linear-gradient(left, #7d7e7d 0%, #0e0e0e 100%);
		/* Chrome10-25,Safari5.1-6 */
	}


	.app-theme-high-contrast .mat-card {
		background: #000;
	}
	

	.app-theme-high-contrast .mat-card {
		color: rgba(255, 251, 251, 0.87);
	}


	.app-theme-high-contrast .mat-drawer {
		background-color: gray;
		color: rgba(255, 255, 255, 0.87);
	}


	.app-theme-high-contrast .mat-drawer-container {
		background-color: #282828;
		color: rgba(255, 255, 255, 0.87);
	}


	.buzz-course-summary {
		text-align: left;
		background-color: #282828;
		border-radius: 10px;
		min-height: 125px;
		padding: 20px
	}

	.app-theme-high-contrast .mat-card-subtitle {
		color: #f44336;
	}

/*Grade percentage color on course cards when passing*/
	xli-grade-editor .xli-grade.xli-pass, xli-score .xli-grade.xli-pass {
		color: #2196f3;
	}

/*Grade percentage color on course cards when failing*/
	xli-grade-editor .xli-grade.xli-fail, xli-score .xli-grade.xli-fail {
		color: #f00;
	}
	
	.element.style {
		border-color: #f00;	
		background: #282828;
	}


	/*Course Card Class Name Text*/
	.app-theme-light-blue .mat-card
	{
		color: rgba(255, 255, 255, 1);
        }


	.course-card[_ngcontent-c17]
	{
		background: #282828;
	}
/*Course card backgrounds and borders*/
	.app-theme-light-blue .mat-card
	{
		background: #28282833;	
      	border-style: groove;
	    border-width: 0px;
	    border-color: #2196f3;
    }
	
/*Course Card enrollment period text*/
	.app-theme-light-blue .mat-card-subtitle
	{
		color: rgba(255, 255, 255, 0.87);	
	}	

/*Text color for your grade at the top of the agenda in echo*/
	.buzz-course-home .buzz-course-metrics .buzz-course-metric2
	{
		color: #ff00ea;
	}
	
/*The box surrounding the top of the agenda, where the title of the course and the course image is*/
	.buzz-course-home .buzz-course-summary .buzz-course-summary-bottom, .buzz-course-home .buzz-course-summary .buzz-course-summary-top
	{
		background: rgba(255, 255, 255, 0);	
	}
	
/*The Class title and enrollment period in the agenda*/
	.buzz-course-home .buzz-course-details
	{
		color: #fff;

	}

/*The Big ol box around the agenda */
	.buzz-course-home .buzz-course-summary
	{
		background: rgba(255, 255, 255, 0);	
        border-style: groove;
	    border-width: 1px;
	    border-color: #2196f3;
	}
	
/*The color of the title of the up next activity at the bottom of the agenda*/
	.buzz-course-home .buzz-course-summary .buzz-course-summary-item .item-title
	{
		color: #fff;	
	}
	
/*The color of the circle with your grade in it in the agenda*/
	.buzz-course-home .buzz-course-metrics:before
	{
		background: #282828;
	}

/*The text that says agenda in the top left of the agenda*/
	.buzz-course-home .buzz-agenda-ct .buzz-agenda-header h3, .buzz-course-home .buzz-project-ct .buzz-project-header h3
	{
		color: white;	
	}

/*The background for the tabs with the date on them at the top of the agenda*/
	.buzz-course-home .buzz-agenda-ct .buzz-agenda-header .buzz-agenda-choose-day .buzz-agenda-date
	{
		background: #28282800;	
	}

	.buzz-course-home .buzz-agenda-ct .buzz-agenda-header .buzz-agenda-choose-day .buzz-agenda-date.today{
		color: #ff00f7;
	}

	.buzz-course-home .buzz-agenda-ct .buzz-agenda-content
	{
		border-style: groove;
		border-width: 5px;
		border-color: #2196f3;	
		background: white;	
	}

	.buzz-course-home .buzz-agenda-ct .buzz-agenda-header .buzz-agenda-choose-day .buzz-agenda-date .day-title
	{
		color: #2196f3;
	}

	.buzz-course-home .buzz-agenda-ct .buzz-agenda-header .buzz-agenda-choose-day .buzz-agenda-date .date-title
	{		
		color: #2196f3;
	}

.score[_ngcontent-c44] .objective-detail[_ngcontent-c44] .objective-title[_ngcontent-c44]
{
color: white;
}

xli-grade-editor .xli-grade .xli-objective .xli-objective-title, xli-score .xli-grade .xli-objective .xli-objective-title
{
color: white;
}

.score[_ngcontent-c44]
{
color: #2196f3;
}

.main[_ngcontent-c42]
{
background: #282828;
}

.score[_ngcontent-c20] .objective-detail[_ngcontent-c20] .objective-title[_ngcontent-c20]
{
color: white;
}

.buzz-pass-color
{
color: #2196f3;
}

.app-theme-light-blue .mat-toolbar.mat-primary
{
color: #2196f3;
}

.buzz-side-bar > div 
{
	background: #282828;
	color: #2196f3;
}

.buzz-sidebar-label, .buzz-to-do-list-ct .buzz-to-do-header, .buzz-to-do-list-ct .empty, .buzz-to-do-list-ct .loading 
{
	color: white;	
}

.buzz-side-bar xli-course-tree .xli-tree-content .xli-item-title 
{
	color: white;	
}

xli-grade-editor .xli-grade .xli-status, xli-score .xli-grade .xli-status 
{
	color: red;	
}

.buzz-search-bar
{
	background: #646464;
}

.buzz-search-bar .body 
{
	color: #646464;	
}

.buzz-search-bar .body input
{
	background: #646464;	
}

.buzz-course-home .buzz-agenda-ct .buzz-agenda-header .buzz-course-home .buzz-folder-home
{
	background: #282828;	
}

/*The title text of the folder that you're viewing*/
.buzz-folder-home .buzz-folder-title-section .buzz-folder-title
{
	color: white;	
}

.buzz-folder-home .buzz-folder-summary .buzz-folder-items-ct, .buzz-folder-home .buzz-folder-summary .buzz-folder-metadata-ct
{
	color: white;	
}

.buzz-folder-home .buzz-folder-summary .buzz-folder-items .buzz-folder-item>a
{
	color: white;	
}

/*The color of the text in the sidebar (usually folder names or assignment titles)*/
div.item-title.ng-binding
{
	color: white;	
}

div.xli-tree-content.xli-toolbar.xli-item-type-folder.xli-selected
{
	color: black;	
}

/*The box behind the folders when you open a folder*/
.buzz-folder-home .buzz-folder-summary
{
	background: #28282800;	
}

/*The text below the agenda where teachers link the syllabus and stuff*/
div.ng-scope.content.fr-view
{
	color: white;	
}
/*some really random boxes at the top of the agenda that shouldn't exist, but it does because echo is bad*/
.buzz-course-home .buzz-agenda-ct .buzz-agenda-header .buzz-agenda-choose-day, .buzz-course-home .buzz-course-title-section, .buzz-folder-home
{
	background: rgba(187, 49, 49, 0);	
}
/*the box around the activity boxes under the adgenda*/
xli-activity-stream .xli-date-group, xli-activity-stream .xli-loading-status>.loading
{
    background-color: #28282833;
}
/*the background behind open activities that you're submitting*/
.buzz-activity-ct {
    position: relative;
    background-color: #b0b0b000;
}

/*the box that surrounds the bottom of an activity that has the open and hide dropbox button in it, as well as the cancel, save, and submit buttons*/
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #f5f5f500;
    border: 1px solid #e3e3e300;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0);
}
/*the text in the stuff on the side boxes with activities and stuff*/
div.item-title.ng-binding {
    color: #646464;
}

/*the box that surrounds an activity/submission box*/
.panel {
    margin-bottom: 20px;
    background-color: #fff0;
    border: 1px solid transparent;
    border-radius: 4px;
    box-shadow: 0 1px 1px rgba(249, 89, 89, 0);
}
