css:
@-moz-document domain("web.okjike.com") {
/*即刻App，每天十小时*/
/*待修*/
:root {
	--bg0: #eae9e7;
	/*背景颜色*/
	--bg1: #f8f7f5;
	/*导航、卡片及其下拉框背景颜色*/
	--bg2: #FDFDFD;
	/*搜索栏下拉 hover 颜色*/
}

html,
body {
	background-color: var(--bg0) !important;
}

.feed-root， 
.topic-info， 
.feed-nav-container {
	background-color: var(--bg0);
}

.menu,
.menu-item,
.menu-submenu-title,
.site-header,
.topic-card,
.search-wrap-dropdown,
.search-wrap-tip,
.search-words,
.popover-inner-content,
.popover-arrow:after,
.modal-header,
.post-creator-body,
.link-uploader,
.post-creator-topic-search,
.user-profile-main,
.user-profile,
.message-card,
.message-link,
.user-activity,
.user-activity-repost-card,
.user-activity-comment-message,
.message-card-modal,
.user-activity-post-link,
.comments-timeline,
.comments-timeline-container,
.comment-creator-small,
.dropdown-menu,
.user-card,
.topic-info-main,
.topic-list,
.search-integrate-banner {
	background-color: var(--bg1);
}
.site-header {
	box-shadow: none;
}

.result-list,
.result-list > li:hover,
.feed-nav,
.feed-nav-tip,
.collection-header,
.tabs-nav-scroll {
	background-color: var(--bg1) !important;
}

.menu-item:hover {
	background-color: var(--bg2);
}

.dropdown-menu:hover,
.dropdown-submenu:hover,
.dropdown-menu-item:hover {
	background-color: var(--bg2) !important;
}


/*Love Jike Extension*/
.comments-timeline,
.comments-timeline-container {
	background-color: var(--bg1) !important;
}

/*Thanks Love Jike Extension*/
}
