css:
@-moz-document domain("psim.us"), domain("play.pokemonshowdown.com"), domain("dawn.psim.us") {
/*------------------------------------
TOC (Table Of Contents)

#Join Room Tab
#Main Menu
#Settings
#Top Bar (Room Tabs)
#PM Box
#Chatrooms
#Userlist
#Teambuilder
#Main Menu Footer (Links)
#Battle Room
	#Left Bar/Right Bar
	#Battle Weather
	#Battle Controls
	#Pokemon/Move Popup
	#Health Bar, Status Bar, Info Bar
#Broadcasts
#Tournaments
------------------------------------*/

:root {
    /* touch these if you want, but it isn't reccomended */
    --white1: #FFF;
    --white2: #F7F7F7;
    --white3: #DDD;
    --black1: #000;
    --black2: #111111;
    --black3: #121212;
    --black4: #131313;
    --black5: #151515;
    --black6: #161616;
    --black7: #1C1C1C;
    --black8: #252525;
    --gray1: #383838;
    --gray2: #777777;
    --gray3: #999;
    /* An: I reccomend pulling these from the image you use as your bg */
    /* An: Don't touch the transparency (the 0.5 and 0.65 values on the t1 and t2 values) */
    /* An: Hex color codes (like #ffffff) can be easily translated into rgb (255, 255, 255) */
    --customcolor1: #FD954E;
    --customcolor2: #405072;
    --customcolor3: #A04032;
    --customcolor4: #31314B;
    --customcolor1t1: rgba(253, 149, 78, 0.5);
    --customcolor1t2: rgba(253, 149, 78, 0.65);
    --customcolor2t1: rgba(64, 80, 114, 0.5);
    --customcolor2t2: rgba(64, 80, 114, 0.65);
    --customcolor4t2: rgba(49, 49, 75, 0.65);
    /* An: some colors look better with gray bgs, this is for that */
    --backgroundcolor: #383838;
    /* An: Change the link of this line to the image you want. Maximum recommended size: 80x80 */
    --icon: url("https://i.imgur.com/9cNjmRY.png");
}
/* An: Any "!important" does not have a space between it and the element it follows/the element before it */
.userbar:before {
    display: block;
    content: "";
    width: 32px;
    height: 32px;
    float: left;
    position: relative;
    top: -5px;
    background: var(--icon);
    background-repeat: no-repeat;
}
/* AN: The code below removes the little head n' shoulders icon by your name */
i.fa.fa-user::before {
    content: "";
}

.icon-user {
    display: none;
}

button:focus,
select:focus {
    outline: 0;
}

.body {
    background: var(--gray1);
}

.mainmenuwrapper {
    background: var(--gray1) var(--background) center middle no-repeat;
    background-size: auto;
    border-right: none;
    background-attachment: fixed;
    background-position: left top;
}

.ps-overlay {
    background: rgba(0, 0, 0, 0.3);
}

.ps-room.ps-room-light {
    background-color: rgba(0, 0, 0, 0);
    border-color: var(--customcolor1);
}

.ps-room.ps-room-light.scrollable {
    background: var(--gray1) center right no-repeat;
    border-color: var(--black1);
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
}

.ps-room.ps-room-light.scrollable[id*="view-"] {
    background: var(--gray1);
}

button[value="/pets validateteam"], 
button[value="/pets viewteams"], 
button[value="/pets saveteam"] {
    color: var(--black1);
}

/*------------------------------------*\
  #Join Room Tab
\*------------------------------------*/

.roomlist a.ilink, 
.roomcounters button {
    border-color: var(--customcolor1);
    box-shadow: 0px 0px 2px var(--customcolor1);
    background: transparent;
    color: var(--customcolor1);
    text-shadow: 0px 0px 2px var(--black3);
    transition: 0.3s;
}

.roomlist a.ilink:hover,
.roomcounters button:hover {
    border-color: var(--customcolor3);
    box-shadow: 0px 0px 2px var(--customcolor3);
    background: transparent;
    color: var(--customcolor1);
    text-shadow: 0px 0px 2px var(--black3);
    transition: 0.3s;
}

button[name="finduser"],
button[name="finduser"]:focus,
button[name="roomlist"],
button[name="roomlist"]:focus,
button[name="closeHide"],
button[name="closeHide"]:focus,
button[name="cancelSearch"],
button[name="cancelSearch"]:focus {
    border-color: var(--customcolor1)!important;
    box-shadow: 0px 0px 2px var(--customcolor1)!important;
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="finduser"]:hover,
button[name="roomlist"]:hover,
button[name="closeHide"]:hover,
button[name="cancelSearch"]:hover {
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="finduser"]:active,
button[name="roomlist"]:active,
button[name="closeHide"]:active,
button[name="cancelSearch"]:active {
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

.roomlist h2 {
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
}

div.newsentry.unread {
    background: var(--white3);
    color: var(--black1);
}

.menugroup {
    background: var(--customcolor2t1);
    border: 1px solid var(--customcolor1);
}

.label {
    text-shadow: 0px 0px 3px var(--black1);
}

/*------------------------------------*\
  #Main Menu
\*------------------------------------*/

.mainmenu .menugroup:nth-child(1) .button,
.mainmenu .menugroup:nth-child(1) .button:focus,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:focus,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:focus,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:focus,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:focus,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:focus,
.select,
.select:focus,
button[name="selectFormat"],
button[name="selectFormat"]:focus,
a[href="//pokemonshowdown.com/users/"],
a[href="//pokemonshowdown.com/users/"]:focus,
button[name="selectFolder"],
button[name="selectFolder"]:focus {
    border-color: var(--customcolor1)!important;
    box-shadow: 0px 0px 2px var(--customcolor1)!important;
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    transition: 0.3s;
}

.mainmenu .menugroup:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover,
.select:hover,
.roomlist a.ilink:hover,
button[name="selectFormat"]:hover,
a[href="//pokemonshowdown.com/users/"]:hover,
button[name="selectFolder"]:hover {
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    transition: 0.3s;
}

.mainmenu .menugroup:nth-child(1) .button:active,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:active,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:active,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:active,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:active,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:active,
.select:active,
.roomlist a.ilink:active,
button[name="selectFormat"]:active,
a[href="//pokemonshowdown.com/users/"]:active,
button[name="selectFolder"]:active {
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    transition: 0.3s;
}

.menugroup .label {
    color: var(--customcolor1);
}

.menugroup .select.teamselect.preselected,
.button.select.teamselect.preselected,
.challenge .select.teamselect.preselected {
    background: var(--customcolor2)!important;
    border-color: var(--customcolor1)!important;
}

.challenge .select.teamselect.preselected:hover {
    background: var(--customcolor2t1)!important;
    border-color: var(--customcolor1);
}

.menugroup .select.teamselect.preselected:hover {
    background: var(--customcolor4)!important;
    border-color: var(--customcolor3)!important;
}

.chat.highlighted {
    background: var(--customcolor1);
}

.teamselect:disabled strong {
    color: var(--customcolor1);
}

.select.preselected,
.select.preselected:disabled {
    color: var(--customcolor1)!important;
}

.button.button.mainmenu4.onlineonly,
.button.button.mainmenu4.onlineonly:focus,
.button.button.mainmenu5.onlineonly, 
.button.button.mainmenu5.onlineonly:focus, 
.button.button.mainmenu1.onlineonly, 
.button.button.mainmenu1.onlineonly:focus, 
button[name="showSearchGroup"],
button[name="showSearchGroup"]:focus {
    border-color: var(--customcolor1)!important;
    box-shadow: 0px 0px 2px var(--customcolor1)!important;
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    transition: 0.3s;
}

.button.button.mainmenu4.onlineonly:hover,
.button.button.mainmenu5.onlineonly:hover, 
.button.button.mainmenu1.onlineonly:hover, 
button[name="showSearchGroup"]:hover {
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    transition: 0.3s;
}

.button.button.mainmenu4.onlineonly:active,
.button.button.mainmenu5.onlineonly:active, 
.button.button.mainmenu1.onlineonly:active, 
button[name="showSearchGroup"]:active {
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    transition: 0.3s;
}

.mainmenu .menugroup:nth-child(1) .button {
    width: 230px!important;
}

.select::after {
    color: var(--gray1)!important;
}

.select:disabled,
.select:disabled:hover {
    border-color: var(--gray1)!important;
    box-shadow: none!important;
    opacity: 1!important;
    background: transparent!important;
}

.select.preselected,
.select.preselected:disabled {
    color: var(--customcolor2)!important;
    border-color: var(--customcolor2)!important;
    background: var(--customcolor2t1)!important;
    text-shadow: none!important;
}

.menugroup .button.disabled,
.menugroup .button.disabled:hover {
    border-color: var(--gray1)!important;
    box-shadow: none!important;
    background: transparent!important;
    color: var(--gray1)!important;
    text-shadow: none!important;
}

.battleform label {
    background: transparent!important;
    color: var(--white1);
}

button {
    border: 1px solid var(--customcolor1);
    box-shadow: 0px 0px 2px var(--black1);
    background: transparent;
    color: var(--customcolor1);
    border-radius: 4px;
}

.btn-nofill-nimbus,
.button-donate2,
button.astext {
    box-shadow: none;
}

/*------------------------------------*\
  #Settings
\*------------------------------------*/

button.button.mainmenu4,
button.button.mainmenu4:focus,
button[name="background"],
button[name="background"]:focus,
button[name="changepassword"], 
button[name="changepassword"]:focus, 
button[name="avatars"],
button[name="avatars"]:focus,
button[name="formatting"], 
button[name="formatting"]:focus, 
button[name="close"], 
button[name="close"]:focus, 
button[name="login"], 
button[name="login"]:focus, 
button[name="logout"], 
button[name="logout"]:focus, 
button[name="refresh"],
button[name="refresh"]:focus,
button[name="joinRoomPopup"],
button[name="joinRoomPopup"]:focus,
button[type="submit"],
button[type="submit"]:focus,
select,
select:focus {
    border-color: var(--customcolor1)!important;
    box-shadow: 0px 0px 2px var(--customcolor1)!important;
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    transition: 0.3s;
}

button.button.mainmenu4:hover,
button[name="background"]:hover, 
button[name="changepassword"]:hover, 
button[name="avatars"]:hover, 
button[name="formatting"]:hover, 
button[name="close"]:hover, 
button[name="login"]:hover, 
button[name="logout"]:hover, 
button[name="refresh"]:hover, 
button[name="joinRoomPopup"]:hover,
button[type="submit"]:hover,
select:hover,
select:active {
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    transition: 0.3s;
}

button.button.mainmenu4:active,
button[name="background"]:active, 
button[name="changepassword"]:active, 
button[name="avatars"]:active, 
button[name="formatting"]:active, 
button[name="close"]:active, 
button[name="login"]:active, 
button[name="logout"]:active, 
button[name="refresh"]:active, 
button[name="joinRoomPopup"]:active,
button[type="submit"]:active,
select:active,
select:active {
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black1)!important;
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    transition: 0.3s;
}

button[name="setBg"],
button[name="setBg"]:focus {
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="setBg"]:hover {
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="setBg"]:active {
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="setAvatar"],
button[name="setAvatar"]:focus {
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="setAvatar"]:hover {
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    background-color: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

button[name="setAvatar"]:active {
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    background-color: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}

option {
    background: var(--black1);
}

option:hover {
    background: var(--customcolor2);
}

optgroup {
    background: var(--black1);
}

/*------------------------------------*\
  #Top Bar (Room Tabs)
\*------------------------------------*/

.maintabbarbottom {
    background: linear-gradient(to bottom, rgba(0, 0, 0, .4) 0%, rgba(0, 0, 0, 0) 100%);
    border: none;
    box-shadow: none;
    z-index: 2;
}

.header {
    height: 56px;
    background: var(--black1);
}

.header .logo {
    float: left;
    margin: 7px 0px 0 2px;
}

.userbar .username {
    color: var(--white1);
    text-shadow: 1px 1px 0px #151515, 1px -1px 0px #151515, -1px 1px 0px #151515, -1px -1px 0px #151515;
}

.userbar button.icon, 
.userbar button.icon:focus, 
button[name="openSounds"],
button[name="openSounds"]:focus,
button[name="openOptions"],
button[name="openOptions"]:focus,
button[name="toggleIgnoreUser"], 
button[name="toggleIgnoreUser"]:focus, 
button[name="report"],
button[name="report"]:focus {
    border-color: var(--customcolor1)!important;
    box-shadow: 0px 0px 2px var(--customcolor1)!important;
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: none!important;
    transition: 0.3s;
}

.userbar button.icon:hover, 
button[name="openSounds"]:hover,
button[name="openOptions"]:hover, 
button[name="toggleIgnoreUser"]:hover, 
button[name="report"]:hover {
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: none!important;
    transition: 0.3s;
}

.userbar button.icon:active, 
button[name="openSounds"]:active,
button[name="openOptions"]:active, 
button[name="toggleIgnoreUser"]:active, 
button[name="report"]:active {
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: none!important;
    transition: 0.3s;
}

select {
    border-color: var(--customcolor1);
    background: transparent;
    background-color: transparent;
    color: var(--customcolor1);
    border-radius: 4px;
}

.tabbar .closebutton,
.tabbar a.cur + .closebutton {
    top: 17px;
    transition: 0.3s;
}

.closebutton:hover {
    color: var(--customcolor3);
    transition: 0.3s;
}

.tabbar .closebutton:hover {
    color: var(--customcolor3);
    text-shadow: 0px 0px 3px var(--customcolor3);
    transition: 0.3s;
}

.roomtab.button.cur.subtle-notifying.closable {
    color: var(--customcolor1)!important;
}

.tabbar .closebutton {
    color: var(--customcolor1);
}

.minimizebutton:hover {
    color: var(--customcolor3);
}

.closebutton,
.minimizebutton {
    color: var(--customcolor1);
    box-shadow: none;
}

.tabbar a.button.notifying,
.tabbar a.button.notifying:hover {
    background: var(--black3);
    transition: 0.3s;
}

.tabbar a.button.subtle-notifying {
    color: var(--white1);
}

.tabbar .button.notifying {
    color: var(--customcolor2);
    background: var(--black8)!important;
    transition: 0.3s;
}

.tabbar .button.notifying:hover {
    color: var(--customcolor4);
    transition: 0.3s;
    text-shadow: none;
}

.maintabbar .inner {
    padding-top: 0px;
    height: 56px;
}

.tabbar.maintabbar {
    margin-left: 170px;
    margin-right: 0px;
    overflow: hidden;
    height: 60px;
    background-position: right;
    background-repeat: no-repeat;
}

.tabbar a.button.cur,
.tabbar a.button.cur:hover,
.tabbar a.button {
    box-shadow: none;
    border: none;
    cursor: default;
    top: 0px;
    border-radius: 5px;
    text-shadow: none;
    margin-left: 5px;
    margin-top: 15px;
    transition: 0.3s;
}

.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    background: var(--black1);
    box-shadow: none;
    border: none;
    cursor: default;
    top: 0px;
    border-radius: 5px;
    text-shadow: none;
    margin-left: 5px;
    margin-top: 15px;
    color: var(--white1);
    transition: 0.3s;
}

.tabbar a.button {
    background: var(--black5);
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
    border: none;
    transition: 0.3s;
}

.tabbar a.button:hover {
    background: var(--black3);
    color: var(--gray3);
    text-shadow: 0px 0px 3px var(--gray1);
    transition: 0.3s;
}

.tabbar .button.subtle-notifying {
    color: var(--customcolor1)!important;
    background-color: var(--black8);
    transition: 0.3s;
}

.tabbar .button.subtle-notifying:hover {
    color: var(--customcolor3)!important;
    transition: 0.3s;
}

/*------------------------------------*\
  #PM Box
\*------------------------------------*/

.pm-window h3 {
    background: var(--customcolor2);
    border-color: var(--black1);
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
    transition: 0.3s;
}

.pm-window h3.pm-notifying {
    background: var(--customcolor2);
    border-color: var(--customcolor1);
    color: var(--customcolor1);
}

.pm-window h3.pm-notifying:hover {
    background: var(--customcolor4);
    border-color: var(--customcolor3);
    color: var(--customcolor1);
    transition: 0.3s;
}

.pm-window h3:hover .minimizebutton {
    color: var(--customcolor3);
}

.pm-window h3 .closebutton:hover + .minimizebutton {
    color: var(--customcolor1)!important;
}

.pm-window h3 small,
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
    color: var(--customcolor1);
    transition: 0.3s;
}

.pm-window h3 small:hover,
.pm-window h3 .closebutton:hover,
.pm-window h3 .minimizebutton:hover {
    color: var(--customcolor3);
    transition: 0.3s;
}

.pm-window h3:hover {
    color: var(--customcolor3);
    transition: 0.3s;
}

