}

.dark #tooltipwrapper .tooltipinner {
    position: relative;
    transition: fade-in 0.3s;
}


.dark div#tooltipwrapper div.tooltip {
    opacity: 0!important;
    -webkit-transition: 0.3s;
    transition: 0.3s;
}

div#tooltipwrapper div.tooltipinner div.tooltip {
    opacity: 1!important;
    position: fixed;
    bottom: auto;
    top: 569px;
    left: 197px;
    width: 368.5px;
    border: 1px solid var(--black1);
    background: var(--customcolor1t2);
    -webkit-transition: 0.3s;
    transition: opacity 0.3s;
    border-radius: 5px;
}


.dark #tooltipwrapper .tooltip h2 {
