\*------------------------------------*/

.dark .statbar {
    position: absolute;
    display: none;
    width: 186px;
    background: url(http://i.imgur.com/eYsfnK4.png);
    background-repeat: no-repeat;
    background-position: 0px 1px;
    min-height: 40px;
    padding: 2px 4px 2px 26px;
}

.dark .statbar strong {
    position: absolute;
    left: 30px;
    top: 6px;
    font-size: 9px;
    font-weight: normal;
    display: block;
    text-align: center;
    color: var(--white1);
