css:
@-moz-document domain("www.hulu.com") {
#content {
    background-color: #000 !important;
}

.EditorialCollection__slider-overflow-hider {
 	background-image: linear-gradient(to right,#00000af7 97%,#00000af7 100%);   
}

.Nav__link .fill {
 	fill: #e3dfdf !important;   
}

.SimpleCollection__title,
.Tile__title--clickable,
.GlobalNav__profile-first-item,
.GlobalNav__profile-first-item--profile-name,
.GlobalNav__add-account,
.Nav__link,
.Subnav__item.active,
.Select-value,
.Select-option.is-focused,
.DetailsAbout__name,
.Tile__title,
.ViewAllLink__btn-text,
.BrowseMenu__item,
.PortraitCollection__title,
.Hub__title,
.GenericTileContent__title {
 	color: #e3dfdf !important;
}

.DetailsMasthead__container .Tile__title {
    color: #fffc !important;
}

.SearchBar > input[type=text],
.FullTextSearch__Entry,
.ListItem__HighlightText--Highlighted {
 	color: #fff;   
}

.BrowseMenu__item--active {
    color: #e3dfdf !important;
	-webkit-text-stroke-color: #e3dfdf !important;;
    border-bottom-color: #e3dfdf !important;;   
}

.Nav__dropdown > .Nav__container:hover,
.GlobalNav__add-account:hover,
.GlobalNav__logout:hover,
.ListItem--Selected {
 	background-color: #3b3e4680 !important;   
}

.GlobalNav__profile-first-item:hover .GlobalNav__letter {
	background-color: #5e6573 !important;
}

.LevelTwo__container,
.DetailsBody__about,
.DetailsDropdown__single,
.Select-value,
.Select-menu,
.Select-option.is-focused {
	background-color: #08090c !important;
}

.Hub__collections .SimpleCollection,
.ListItem,
.Select-value {
 	border-bottom: 1px solid #e3dfdf !important;   
}

.Nav.GlobalNav {
 	box-shadow: 0 1px 0 0 #e3dfdf99;
}

.Nav__dropdown {
border: 1px #e3dfdf99 !important;   
}

.BrowseMenu__genres {
 	border-left: 1px solid #e3dfdf !important; 
}

.GlobalNav__profile-first-item,
.GlobalNav__add-account,
.SingleNetwork__subnav {
    border-bottom: 1px solid #e3dfdf99 !important;
}
.GenericTileContent__title.GenericTileContent__title {
    color: #e3dfdf !important;
}
.HoverPopout {
    background-color: #121417;
}
.GenericTileContent__metadata {
    color: #e3dfdf;
}
.GenericTileContent__description {
    color: #b6bbc9;
}
.GenericTileContent__action {
	color: #b6bbc9;
}
.ProfileDropdown__links .ProfileDropdown__account, .ProfileDropdown__links .ProfileDropdown__helpCenter, .ProfileDropdown__links .ProfileDropdown__logout, .ProfileDropdown__links .ProfileDropdown__manageProfiles {
	color: #b6bbc9;
	line-height: 32px;
}
.ProfileDropdown__links .ProfileDropdown__account:hover, .ProfileDropdown__links .ProfileDropdown__helpCenter:hover, .ProfileDropdown__links .ProfileDropdown__logout:hover, .ProfileDropdown__links .ProfileDropdown__manageProfiles:hover {
	color: #e2e4e9;
	cursor: pointer;
}

button::-moz-focus-inner, input::-moz-focus-inner {
    border: 0;
    padding: 0;
}
.MyStuffAction--added .MyStuffAction__button {
    background-color: #292c33;
    box-shadow: inset 0 0 0 0 #292c33;
}
.MyStuffAction__button {
	background-color: #292c33;
	box-shadow: inset 0 0 0 2px #292c33;
}
.MyStuffAction__line-1, .MyStuffAction__line-2 {
	background-color: #fff;
}
.StopSuggesting__btn:hover {
    transform: scale(1.1);
    box-shadow: inset 0 0 0 2px #fff;
}
.StopSuggesting__btn:hover .StopSuggesting__line-1 {
    background-color: #fff;
}
.SingleNetworkMasthead__arrow--right circle, .SingleNetworkMasthead__arrow--left circle, .Slider__button--next circle, .Slider__button--previous circle {
    fill: #000 !important;
}
.SearchResultContainer__Title {
    color: #e3dfdf;
}
.RecentSearch__Item {
	background-color: transparent;
	color: #b6bbc9;
}
.Footer__logo {
	color: #868d9b;
}
a {
    color: #b6bbc9 !important;
}
body {
    color: #b6bbc9;
}
a {
	outline: 0;
	color: #fff !important;
}
.Nav.GlobalNav {
    box-shadow: 0 1px 0 0 #0009;
}

.ViewAllLink__right-arrow::after {
    content: "";
    border: 2px solid #D5D5D5;
    border-width: 0 2px 2px 0;
    margin: 0 5px;
}
.Hub__collections .SimpleCollection, .ListItem, .Select-value {
    border-bottom: 1px solid #B6BBC98F !important;
}
.RecentSearch__Clear {
	color: #b6bbc9;
	background-color: transparent;
}
.RecentSearch__Clear:hover {
    color: #B6BBC9E8;
}
.SearchBar {
	border-color: #b6bbc9;
}
.ListItem__Content__HighlightText--Normal {
	color: #fff;
}
.OffsiteView h3 {
	color: #e3dfdf;
}
.SearchBar__ClearButton {
	color: #b6bbc9;
}
.RecoView__title mark {
	color: #e3dfdf;
	background-color: #000;
}
.RecoView__title {
	color: #b6bbc9;
}
.Tile__description, .Tile__metadata {
	color: #b6bbc9;
}
mark {

	color: #e3dfdf;
}
.FullTextSearch__Entry {
	border-bottom: 1px solid #b6bbc9;
}
.Tile__title.Tile__title mark {
	background-color: #000;
}
.UpsellActionTextView {
	color: #4289E2;
}
.Tile__action {
	color: #b6bbc9;
}
.OffsiteView .Item {
	background-color: #000;
}
.OffsiteView .Item__Title--Normal {
	color: #b6bbc9;
}
.OffsiteView .Item__Title--Highlighted {
	color: #e3dfdf;
}
.Loader__bar {
	display: none !important;
}
.MyStuffBanner__background {
	background-color: #000;
}
.MyStuffBanner__text-container {
	color: #e3dfdf;
}
.MyStuffBanner__description {
	color: #b6bbc9;
}
.LevelOne--gray {
	background-color: #000;
}
.MyStuff__collection-title {
	color: #e3dfdf;
}
.Subnav__item {
	color: #e3dfdf;
}
.DetailsAbout__head {
	color: #b6bbc9;
}
.DetailsAbout__description {
	color: #b6bbc9;
	line-height: 24px;
}
.DetailsCollection__items--full-width .Tile__action {
	color: #b6bbc9;
}
.Select-option {
	color: #b6bbc9;
}
.DetailsAbout__meta {
	margin-top: 32px;
	color: #B6BBC9BD;
}
.BrowseMenu__genres {
	border-left: 1px solid #2A2C2D !important;
}
.MyStuffAction--added .MyStuffAction__line-1, .MyStuffAction--added .MyStuffAction__line-2 {
	background-color: white;
}
.SearchBar button {
	background-color: #000;
}
body, html {
	background-color: #000;
}
.MastheadTile {
	background-color: #000;
}
.LoadingHomePage__portraitRectangle, .LoadingHomePage__regularRectangle {
	background-color: #000;
}
.LoadingHomePage__subtitleLine, .LoadingHomePage__titleLine {
	background-color: #000;
}
.LoadingHomePage__portraitTitle, .LoadingHomePage__simpleTitle {
	background-color: #000;
}
.MyStuffAction__button {
	background-color: #121417;
	box-shadow: inset 0 0 0 2px var(--button--light);
}

.MyStuffAction--added .MyStuffAction__button {
	background-color: #121417;
	box-shadow: inset 0 0 0 2px var(--button--light);
}
.Nav.GlobalNav {
	box-shadow: 0 1px 0 0 #0000;
}
body.night {
	--background--nav: black;
}
body {
	--white--97: rgba(193, 66, 66, 0);
}
.Footer {
	background-color: #000;
}
.MyStuffAction--added .MyStuffAction__button {
	background-color: #121417;
	box-shadow: inset 0 0 0 2px var(--button--light);
}
.Slider__button--next, .Slider__button--previous {
	filter: invert(100%);
}
.Slider__button--next {
	box-shadow: 2px 4px 10px 0 #fff;
}
.Hub__collections .SimpleCollection, .ListItem, .Select-value {
	border-bottom: 1px solid #000 !important;
}
.SearchBar > input[type="text"], .FullTextSearch__Entry, .ListItem__HighlightText--Highlighted {
	background-color: #000;
}
.SearchBar, .SearchBarContainer {
	background-color: #000;
}
}
