css:
@-moz-document domain("okjike.com")
{

/*评论区*/
.comments-timeline {
    background:#fff;
    width:100%;
}
.comments-timeline-create{
    width:100%;
}
.comments-timeline-container{
    width:100%;
}
.comment-card {
    padding-left:0px !important;
}
.comment-card-main {
    border-bottom: .5px solid #fff;
}
.comment-card-main-pictures-item{
    background-color:#fff !important;
}
.comment-card-main-pictures-item img {
    cursor: pointer !important;
    margin:auto 0 !important;
    transition: all 0.6s;
    top:0;
    left:0;
    transform:translate(0,0);
}
.comment-card-main-pictures-item img:hover {
    transform: scale(1.4);
    z-index:999;
}
/*发评论*/
 .comment-creator-left ,.comments-timeline .user-avatar, .comment-card-left {
    width:46px !important;
    height:46px !important;
}
.comment-creator-main{
    margin-top:1px !important;
}
.comment-creator-main-input {
    box-shadow:none !important;
    background:#f0f3f5;
    border-radius:3px;
    margin-top:2px;
    font-size:14px !important;
    float:right;
    width:100%;
    padding:10px 0 10px 26px;
}
.comment-creator-main-input:active {
    box-shadow:none !important;
    outline:none;
}
.comment-creator-main-input .input{
    font-size:14px !important;
}
.comment-creator-main-operation {
    width:0;
    float:left;
    position:absolute;
}
.comment-creator-main-operation .btn-primary {
    margin:-11px 0 0px -94px;
    width:46px;
    height:46px;
    border-radius:23px;
    font-size:0;
    
    background:#03a9f4;
    color:#fff;
}
.comment-creator-main-operation .btn-disabled {
    background:transparent !important;
    color:transparent;
    border:transparent !important;
}
.comment-creator-main-operation .btn-primary:before {
    content:'send';
    font-size:14px !important;
    width:30px !important;
    line-height:36px;
    margin-left:-9px;
}
.comment-creator-main-operation .btn-link{
    font-size:0;
    margin:-6px 0 0 0;
}
.comment-creator-main-operation .is-flex div{
/*     width:46px;
    height:46px;
    border-radius:50%; */
    background:#ffe411;
    
/*     margin:-10px 0 0 -94px; */
}
.comment-creator-main-operation .content-length{
    color:#333 !important;
}
.comment-creator{
    border-bottom:none !important;
}
.comment-creator-small{
    padding: 15px 0px 15px 10px!important;
}
/*楼中楼*/

.sub-comment-timeline-reply .comment-creator-left{
/*     display:none !important; */
}
.sub-comment-main-before span:nth-child(5){
    color:red;
}

.sub-comment-timeline-comment-item-creator{
background:transparent !important;
    margin: 10px 0 0px 10px;
}
/*看评论*/
 .comments-timeline-container {
    border-top:none;
}
.comment-card-header-screenname {
    font-weight:600;
    font-size:14px;
    line-height: 20px !important;
    margin-top:-6px !important;
}
.user-avatar-content {
    box-shadow:none;
}
.comment-card-main {
    font-size:14px;
}
.readable-content-collapse {
    font-size:14px !important;
    line-height: 20px !important;
}
.comment-card-main-operation {
    width:auto;
    margin-top:-22px;
}
.comment-card-comment-section .is-hidden {
    visibility:hidden;
}
.sub-comment {
    padding:2px 10px !important;
}
.sub-comment-timeline {
    background:#fff;
    margin:10px 20px 0 0;
}
.sub-comment-timeline-more,.sub-comment-timeline-comment-item ,.sub-comment-timeline-reply,.sub-comment-timeline-comment-item-creator{
    border-left: 2px solid #e9ecee;
    border-bottom: 0 !important;
    border-radius:0px !important;
}
.sub-comment-main-screenname {
    font-weight:600;
    font-size:14px;
    line-height: 20px !important;
    color:#333;
}
.sub-comment-main-picture-tip{
    font-size:14px !important;
}
.sub-comment-footer{
    width:170px  !important;
    text-align:inline !important;
}
.sub-comment-footer-time{
    
}
.sub-comment-footer-operation-item{
    color:#ccc ;
}
.sub-comment-footer-operation-item:hover{
    color:#ccc ;
}
.sub-comment-footer-operation-item:nth-child(1):hover,.sub-comment-footer-operation-item:nth-child(2):hover{
    color:#ccc !important;
}
.sub-comment-footer-operation-item:nth-child(1),.sub-comment-footer-operation-item:nth-child(2){
    color:#ccc !important;
}
.sub-comment-footer-operation{
}
.sub-comment-footer-operation .sub-comment-footer-operation-item{
    visibility:visible !important;
    margin:0 10px 0 0;
}
.sub-comment-footer-operation-icon{
    display:none;
}
.sub-comment-footer-operation-num{
    width:50px !important;
    margin-left:0;
    float:left;
}
.sub-comment-footer-operation-num:before{
    content:'赞 ';
}
/*还有xx条评论*/
.sub-comment-timeline-more-num{
/*     color:#a2b5c2; */
    font-weight:600;
    font-size:14px !important;
}
.comments-timeline-more {
    border-top:none;
}
}
