css:
@-moz-document regexp("https?://moodle.th-brandenburg.de/.*") {

    /* Logo */
    .logo {
        background: url(https://i.imgur.com/sb1IlYw.png) 0 0 no-repeat !important;
    }
    
    /* Main */
    body {
        background-color: rgb(20, 20, 20);
        color: rgb(200, 200, 200);
    }
    
    .well {
        background-color: rgb(40, 40, 40) !important;
        border-color: rgb(20, 20, 20);
    }
    .info {
        background-color: rgb(40, 40, 40) !important;
        border-color: rgb(20, 20, 20) !important;
    }
    
    .breadcrumb {
        background-color: rgb(40, 40, 40) !important;
    }
    
    .breadcrumb>li {
        text-shadow: none;
    }
    
    .node_category {
        background-color: rgb(40, 40, 40) !important;
        border-color: rgb(20, 20, 20) !important;
    }
    
    .block {
        background-color: rgb(40, 40, 40);
        border-color: rgb(20, 20, 20);
    }
    
    .menu {
        background-color: rgb(40, 40, 40) !important;
        color: rgb(200, 200, 200);
    }
    
    .filler {
        background-color: rgb(20, 20, 20) !important;
    }
    
    .menu a {
        color: rgb(200, 200, 200) !important;
    }
    
    .coursebox {
        background-color: rgb(40, 40, 40) !important;
        border-color: rgb(20, 20, 20);
    }
    
    .coursebox.collapsed {
        border: none !important;
        margin-bottom: 5px !important;
    }
    
    #page-footer {
        border-color: rgb(40, 40, 40);
    }
    
    /* Messages */
    .popover-region-container {
        background-color: rgb(40, 40, 40);
        border-color: rgb(20, 20, 20);
    }
    .content-item-container {
        border-color: rgb(20, 20, 20);
    }
    .popover-region-header-container {
        border-color: rgb(20, 20, 20);
    }
    .popover-region-footer-container {
        background-color: rgb(40, 40, 40);
        border-color: rgb(20, 20, 20);
    }
    .messaging-area {
        border: none !important;
    }
    .messaging-area-container {
        background-color: rgb(40, 40, 40);
    }
    .messages-header {
        border-bottom-color: rgb(20, 20, 20) !important;
    }
    .message {
        background-color: rgb(40, 40, 40);
        border-color: rgb(20, 20, 20);
    }
    
    /* Content */
    .current {
        background-color: rgb(20, 20, 20) !important;
    }
    
    .course-content {
        border: none !important;
    }
    
    li.section.main {
        border: none !important;
    }
    
    hr {
        border: none !important;
    }
}
