css:
@-moz-document regexp("^https?://www.bilibili.com/video/av\\d(.*)"), regexp("^https?://bangumi.bilibili.com/(.*)/play#(.*)"), regexp("^https?://www.bilibili.com/watchlater/#/av(.*)"), regexp("^https?://www.bilibili.com/bangumi/play/(.*)") {
body {
    background-image: url(https://miao.su/images/2018/10/13/64529766_p0a9c13.png) !important;/*id=64529766*/
    background-position: center bottom !important;
    background-size: cover !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
}
.bui-slider:hover .bui-track.bui-track-video-progress{
    background: url(https://miao.su/images/2018/06/28/2b6508.gif) no-repeat; 
}
bilibili-player-video-progress-detail{
    background: url(https://miao.su/images/2018/06/28/2b6508.gif) no-repeat; 

}
.bui-slider .bui-track .bui-thumb .bui-thumb-dot{
    display:none;
}
.bilibili-player .bilibili-player-area .bilibili-player-video-control .bilibili-player-video-progress:hover .bilibili-player-video-progress-detail{
    opacity:0.0;
    visibility:hidden;
}
.bui-slider .bui-track .bui-bar-wrap .bui-bar{
    background:#CC3300
}
.bui-thumb{
    width:30px;
    height: 30px;
    top: 0px;
    left: -8px;
    position:relative;
    content: "";
    line-height: 28px;
    background: url(https://miao.su/images/2018/06/28/2b6508.gif) no-repeat; 
    display:inline;
}
.bui-slider .bui-track.bui-track-video-progress {
background: url("https://miao.su/images/2018/06/26/7d2c81.png") repeat-x !important; 
}
.bilibili-player-video-control{
    background: url(https://miao.su/images/2018/10/15/quin-s68839b6126.png) no-repeat;
}
.mode-webfullscreen .bilibili-player-video-control{
    background: url(https://miao.su/images/2018/10/13/quin434d2.png) no-repeat;
    border-color:#630400 !important;
}
.mode-fullscreen .bilibili-player-video-control{
    background: url(https://miao.su/images/2018/10/13/quin434d2.png) no-repeat;
    border-color:#630400 !important;
}
.bilibili-player-video-sendbar .bilibili-player-video-inputbar .bilibili-player-video-danmaku-input {
 color:#000000;
 background: url(https://miao.su/images/2018/10/13/texta9075.png) no-repeat;
 font-size:15px;
 min-width:115px;
 width:100%
}

.bilibili-player-video-sendbar .bilibili-player-video-inputbar .bilibili-player-video-danmaku-input{
}
}
