@-moz-document domain("b.dk"), domain("berlingske.dk"), domain("business.dk"), domain("sporten.dk"), domain("lobnu.dk"), domain("dinepenge.dk") {

*[src*="adtech"],
*[action*="adtech"],
*[src*="adform"],
*[id*="FlashID"],
*[id*="td_flash"],
*[id*="AT_"],
*[class*="AT_"],
*[class*="BSFlashAd"],
*[id*="ADFBannerPlace"],
*[id*="ADFExpand"],
*[id*="AT_FLASH"],
*[id*="FlashID"],
*[id*="ebBannerDiv"],
*[id*="quad_ad"],
*[src*="atdmt"],
*[src*="emediate.dk"],
*[src*="doubleclick.net"],
*[src*="2mdn.net"],
.Banners,
.top-banner,
.banner,
.annonce,
.ad,
div.tools li.bookmark,
iframe[src*="jobzonen"],
iframe[id*="rnd"]
{display:none!important}

/* Facebook stuff */
.FBConnectButton,
iframe[src*="facebook"],
*[class*="fb_share"]
{display:none!important}

}

/* Sporten.dk */

@-moz-document domain("sporten.dk") {

.banner,
.banner-top-right
{display:none!important}

#page {
margin-top: -20px !important; }

}
