css:
@-moz-document domain(bakashots.me)
{

html {
    	--text-color: #FFFFFF; /* white */
        --link-color: #b990c0; /* dark pink */
        --light-link-color: #FFE5F2;  /* light pink */
        --dark-link-color: #b990c0; 
        --context-link-color: #FFFFFF; /* white */
        --alt-link-color: #0099cc; /* blue */
        --alert-color: #FF0000; /* red */
        --light-base-color: #cccccc; /* grey */
        --base-color: #33393B; /* grey */
    	--dark-base-color: #1A1D1E; /* dark grey */
    	--darker-base-color: #080A0A; /* darker grey */
    	--title-color: #854C9E; /* violet */ 
        --bonus-color: #AAAA00; /* dark yellow */
        
        --quote-border-color: #d1a2ff; /* purple */
        --top-10-border-color: #aaaaaa; /* grey */
        --quality-variants-b-color: #1e69cc; /* some blue */
	}  

	/*
     *
     *	BAKASHOTS
	 *
     */
     
	body	{
    	color: var(--text-color) !important;
   		background-color: var(--dark-base-color) !important;
        background-image: none !important
	}
    
    /*
    	links
    */

	body a	{
    	color: var(--link-color);
        /* font-weight: bold; */
  	}
    
    .tags a{
    	color: var(--dark-link-color) !important;
    }

    /*
    	images
    */
	.allImagesWrapper, .myImagesWrapper		{
    	color: var(--text-color) !important;
   		background-color: var(--dark-base-color) !important;
	}
    
    #header, .extra, .reportButton	{
    	background-color: var(--base-color) !important;
    }
     
    #allImagesContainer div, .myImagesWrapper div	{
    	border-top-color: var(--light-base-color) !important;
        border-bottom-color: var(--light-base-color) !important;
        border-left-color: var(--light-base-color) !important;
        border-right-color: var(--light-base-color) !important;
        box-shadow: 0px 0px 0px #FFFFFF !important
    }    
    
    #myImagesMenu, menu, #closeBBCodesDisplayer		{
    	background-color: var(--base-color) !important;
    }
    
    menu li:hover, #closeBBCodesDisplayer:hover	{
    	background-color: var(--title-color) !important;
    }
    
    /*
    	report
    */
    
    .fixedWindowCentered	{
   		background-color: var(--base-color) !important;
        box-shadow: 0px 0px 0px #FFFFFF !important;
    }  
    
    #reportReason	{
    	color: var(text-color);
   		background-color: var(--dark-base-color) !important;
    }
    
    #closeReportReasonDisplayer		{
   		background-color: var(--base-color) !important;
    }  
    
    #closeReportReasonDisplayer:hover	{
   		background-color: var(--title-color) !important;
    }  
     
 	/* 
    	login 
    */
    
 	.loginform, .loginform-header	{
   		background-color: var(--base-color) !important;
        box-shadow: 0px 0px 0px #FFFFFF !important;
    }
    
    /*
    	upload
    */
    
 	#uploadFormContainer div, #uploadFormContainer, .headerImageContainer, #torrentId	{
    	color: var(--text-color) !important;
   		background-color: var(--base-color) !important;
        box-shadow: 0px 0px 0px #FFFFFF !important;
 	}
     
 	.headerImageContainer h1	{ /* FILE UPLOAD text */
    	color: var(--text-color) !important;
    }
     
    .NSFW.container b	{
     	color: var(--alert-color) !important;
 	}   
    
    .filename	{
    	color: var(--text-color) !important;
    }

}
