css:
@-moz-document domain("altcoinstalks.com")
{
  /* code by SmallTalk */

:root {
	--bg-color: #2F3238;
	--bg-color-lv2: #26292E;
	--bg-color-lv3: #141518b3;
	--text-color: #ffffff;
	--text-color-lv2: #d1d1d1;
	--text-color-lv3: #cf74b5;
	--link-color: #79a6ff;
	--border-line: solid 0.1em #37557680;
	--border-line-lv2: solid 0.3em #37557680;
	--bg-link-active: #fd7008;
	--bg-link: #4a4b4b;
}

body {
	background: var(--bg-color);
	color: var(--text-color);
}

td, th, tr {
    color: var(--text-color-lv2);
}

.table_list tbody.content td.children {
    color: var(--text-color-lv3);
}

input, button, select, textarea {
    color: var(--text-color);
    background: var(--bg-color) !important;;
}

a:link, a:visited {
    color: var(--link-color);
}

.username h4 {
    color: var(--bg-link-active);
}

.username span {
    color: var(--text-color-lv3);
}

.poster h4, .poster h4 a {
    color: var(--bg-link-active);
}

.poster {
    border-right: var(--border-line);
}

.stickybg {
    background: var(--bg-color-lv3);
}

.stickybg2 {
    background: var(--bg-color-lv3);
}

.greeting span {
	color: var(--bg-link-active);
}

#header {
    background: var(--bg-color-lv2);
}

.avatar > img {
	border: var(--border-line);
}

.frame {
	background: var(--bg-color-lv2) !important;
}

.navigate_section ul {
    border-top: var(--border-line);
}

.inner {
    border-top: var(--border-line);
}

.signature, .custom_fields_above_signature {
    border-top: var(--border-line);
}

.dropmenu li a {
	color: var(--text-color);
	font-weight: bold;
}

.dropmenu li a.active {
    background: var(--bg-link-active);
}

.dropmenu li a.active span.firstlevel {
    background: var(--bg-link-active);
}

.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel {
    background: var(--bg-link);
}

.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel {
    background: var(--bg-link);
}

.dropmenu li li a:hover, .dropmenu li li:hover > a {
    background: var(--bg-link-active);
}

.dropmenu li li {
    border-left: var(--border-line);
    border-right: var(--border-line);
}

.dropmenu li ul {
    border-bottom: var(--border-line);
    background: var(--bg-color);
    padding: 0px 0px 0px 0px;
    margin: 0px -5px;
}

.dropmenu li li.additional_items {
    background-color: var(--bg-color);
}

#upper_section {
	border-bottom: var(--border-line);
}

.news > h2 {
	color: var(--text-color);
}

#content_section {
    background: var(--bg-color-lv2);
}

.windowbg2 {
    color: var(--text-color);
    background-color: var(--bg-color);
}

.windowbg, #preview_body {
    color: var(--text-color);
    background-color: var(--bg-color);
}

.floatleft li {
	color: var(--text-color);
}

.cat_bar {
	background: var(--bg-color) !important;
}

.catbg {
	background: var(--bg-color) !important;
}

span.upperframe {
    background: var(--bg-color-lv2);
}

span.upperframe span {
    background: var(--bg-color-lv2);
}

span.lowerframe {
    background: var(--bg-color-lv2);
}

span.lowerframe span {
    background: var(--bg-color-lv2);
}

table.table_grid td {
    border-bottom: var(--border-line);
    border-right: var(--border-line);
}

.description, .description_board, .plainbox {
    border: var(--border-line);
    background: var(--bg-color);
}

.table_frame .table_list td.icon, .table_frame .table_list td.info, .table_frame .table_list td.stats {
    border-right: var(--border-line);
}

.buttonlist ul li a.active {
    background: #e8e8e8 url(https://www.altcoinstalks.com/images/theme/menu_gfx.png) no-repeat 0 -60px;
    color: #000;
    font-weight: normal !important;
}

.buttonlist ul li a.active span {
    background: url(https://www.altcoinstalks.com/images/theme/menu_gfx.png) no-repeat 100% -60px;
}

.buttonlist ul li a.active:hover {
    color: var(--text-color);
}

.buttonlist ul li a:hover {
    background: var(--bg-link-active);
}

.buttonlist ul li a:hover span {
    background: var(--bg-link-active);
}

.table_grid tr.catbg, .table_grid tr.titlebg {
    border-bottom: var(--border-line);
}

tr.catbg th.first_th {
    background: var(--bg-color-lv2);
}

.table_grid tr.catbg, .table_grid tr.titlebg {
    border-bottom: var(--border-line);
}

tr.catbg th.first_th {
    background: var(--bg-color);
}

.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th {
    background: var(--bg-color);
}

tr.catbg th.last_th {
    background: var(--bg-color);
}

.subject a {
	color: var(--text-color);
}

.windowbg2 span.topslice {
    background: var(--bg-color);
}

.windowbg2 span.topslice span {
    background: var(--bg-color);
}

.windowbg span.botslice {
    background: var(--bg-color);
}

.windowbg span.botslice span {
    background: var(--bg-color);
}

.windowbg2 span.botslice {
    background: var(--bg-color);
}

.windowbg2 span.botslice span {
    background: var(--bg-color);
}

.windowbg span.topslice {
    background: var(--bg-color);
}

.windowbg span.topslice span {
    background: var(--bg-color);
}

.roundframe {
    background: var(--bg-color-lv2);
    border-left: var(--border-line);
    border-right: var(--border-line);
}

#upshrinkHeaderIC {
    color: var(--text-color);
}

div.title_barIC {
    background: var(--bg-color);
}

div.title_barIC h4.titlebg {
    background: var(--bg-color);
    color: var(--text-color);
}

div.title_bar {
    background: var(--bg-color);
}

h4.titlebg, h3.titlebg {
    background: var(--bg-color);
}

h3.titlebg a, h3.titlebg, h4.titlebg, h4.titlebg a {
    color: var(--text-color);
}

h3.titlebg a:hover, h4.titlebg a:hover {
    color: var(--bg-link-active);
}

.table_list tbody.content td.info a.subject {
    color: var(--bg-link-active);
}

blockquote.bbc_standard_quote {
    background-color: var(--bg-color-lv2);
}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    color: var(--text-color);
}

blockquote.bbc_alternate_quote {
    background-color: var(--bg-color-lv2);
}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    color: var(--text-color);
    border-top: var(--border-line-lv2);
    border-bottom: var(--border-line-lv2);
}

.counter {
    color: var(--text-color);
}

.lockedbg {
    background: var(--bg-color-lv2);
}

.lockedbg2 {
    background: var(--bg-color-lv2);
}

code.bbc_code {
    background: var(--bg-color-lv2);
}

#footer_section {
	display: none;
}

/* code by Brotman */

img[src^='https://www.altcoinstalks.com/Themes/default/images/english/new.gif'] {
  content: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABoAAAALCAMAAABWHm3CAAAAG1BMVEX9ixP9ixP9ixP/gwD////9iQ38kB39jBb9ixMsB1DsAAAAA3RSTlN9IpNo67sDAAAARElEQVR42n2PQQoAIAgE1yzt/y8uK6JAHUQPw4qCoOwghQAOAHqk1EJ1soe1U9y2uhUoI0wZZ+unVuxVyYXZXwTxhIAGIFwDpjne294AAAAASUVORK5CYII=);
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/upshrink.png'],
img[src^='https://www.altcoinstalks.com/Themes/default/images/upshrink2.png'] {
  max-width: 20px;
  max-height: 20px;
  margin: 8px 15px !important;
  content: url(data:image/svg+xml;base64,)
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/upshrink2.png'] {
  transform: scaleY(-1);
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/collapse.gif'] {
  max-width: 15px;
  max-height: 15px;
  content: url(data:image/svg+xml;base64,);
}

table.table_list a.collapse {
    margin-top: 8px;
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/expand.gif'] {
  max-width: 15px;
  max-height: 15px;
  content: url(data:image/svg+xml;base64,);
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/r1.png'] {
  content: url(data:image/png;base64,);
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/r2.png'] {
  content: url(data:image/png;base64,); 
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/r3.png'] {
  content: url(data:image/png;base64,); 
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/r4.png'] {
  content: url(data:image/png;base64,); 
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/r5.png'] {
  content: url(data:image/png;base64,); 
}

img[src^='https://www.altcoinstalks.com/Themes/default/images/r6.png'] {
  content: url(data:image/png;base64,); 
}

.dropmenu li a,
.dropmenu li a span.firstlevel,
.buttonlist ul li a,
.buttonlist ul li a span {
  border-radius:5px;
}

#content_section {
    padding-bottom: 20px;
}
}
