css:
@-moz-document url-prefix("https://localbitcoins.com") {
a:link{color:#b1b1b1 !important}
a:visited{color:#b1b1b1 !important}
a:hover{color:#db8506 !important}
a:active{color:#000000 !important}
a.focus{color:#f00000 !important}

body {
    background-color: #353535 !important;
}

.navbar-default {
    background-color: #1b1b1b;
    border-color: #67676700;
}


.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color: #555;
    background-color: #333;
    border: 1px solid #fff;
}

.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #9a9a9a;
    background-color: #505050;
    background-image: none;
    border: 1px solid #7a515100;
    border-radius: 20px;
}

.search-form .form-group input, .search-form .form-group select {
    border-color: #77777700;
}

.form-group .controls .search-form-button {
    font-weight: bold;
    color: #b1b1b1;
    border-color: #80808000;
    background-color: #1d1d1d;
    border-radius: 50px;
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #424242;
}

body {
    color: #ececec;
    background-color: #f00;
}

.btn-default {
    color: #333;
    background-color: #1d1d1d;
    border-color: #000;
    border-radius: 50px;
    width: 100px;
    height: 32px;
    padding: 0px
}

.btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open>.dropdown-toggle.btn-default {
    color: #333;
    background-color: #1d1d1d;
    border-color: #ff9c0000;
}

.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    padding: 3px;
    border-top: 1px solid #42424200;
}

.adlisting .messagetext {
    color: #c7c7c7;
    border-radius: 20px;
    background-color: #2b2b2b;
    border-color: #00000000;
}

.panel-default>.panel-heading {
    color: #0c0c0c;
    background-color: #000000;
    border-color: #ddd0;
}

.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #2b2b2b;
    border: 1px solid #5a5a5a00;
    border-radius: 20px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);
}

#amountdiv div {
    background-color: #1d1d1d;
    display: inline-flex;
    width: 253px;
    margin-left: 13px;
    margin-bottom: 15px;
    padding: 10px;
    border: 1px solid #e0822100;
    border-radius: 30px;
    background-image: -webkit-linear-gradient(bottom, rgba(0, 0, 0, 0) 4%, rgba(90, 206, 224, 0) 95%);
    
}

.ad-calculator-form-old #amountinput {
    width: 150px;
    border: 1px solid #8880;
}

.ad-calculator-form-old #btcinput {
    width: 150px;
    border: 1px solid #8880;
    font-size: 20px;
}

.ad-calculator-form-old #amountdiv input {
    max-width: 1100px;
    font-size: 20px;
    margin-left: 15px;
}

.send-request-fail-reason {
    color: #e28989;
    background-color: #cc8d8200;
    border: 1px solid #e28989;
    border-radius: 20px;
}

hr {
    border-top: 1px solid #585858;
}

.alert-danger {
    background-color: #f2dede00;
    border-color: #d17b89;
    color: #d17b89;
}

.input-group-addon {
    color: #a2a2a2;
    background-color: #1d1d1d;
    border: 1px solid #ccc0;
    border-radius: 20px;
}

.alert-warning {
    background-color: #0000;
    border-color: #abe495;
    color: #abe495;
}

legend {
    border-bottom: 1px solid #565656;
}

.btn-primary {
    color: #fff;
    background-color: #337ab7;
    border-color: #2e6da4;
    border-radius: 20px;
}

#profile-info .shaded {
    background: #2b2b2b;
}

.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
    color: #555;
    background-color: #2f2f2f;
}

tr.clickable:hover td {
    background: #535353 !important;
    cursor: pointer;
}

.dropdown-menu {
    background-color: #1e1e1e;
    border: 1px solid #8d8d8d;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 20px;
    
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    color: #262626;
    background-color: #8d8d8d;
}

.dropdown-menu .divider {
    background-color: #404040;
}

.panel-default>.panel-heading {
    color: #f00;
    background-color: #222;
    border-color: #f000;
}

.panel-default {
    border-color: #333;
}

.ad-calculator-form-old #trade_request_button {
    border-radius: 40px;
}

#request-text-area-wrapper textarea {
    border-radius: 10px;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
    background-color: #363636;
    color: #555;
}
}
