@-moz-document domain("mtbakervapor.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("mtbakervapor.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

/*
.mini-cart-content {
    display: block!important;
}
*/

.btn,
.button,
.buttonstyle,
.cart-container h1,
.confirmation h1,
.featured-products .title,
.header-banner-container .header-promotion,
.label,
.level-1>li a,
.product-tile .product-name,
.pt_content.content-article aside li,
.ui-tabs .ui-tabs-nav li a,
button,
input[type="button"],
label {
	text-transform: capitalize!important;
}

#tabcontent,
.actionBar-section .form-field,
.body .home .homepage-buckets ul li a,
.cart-order-totals,
.checkout-summary-section,
.CurrencyChooser,
.CurrencyList dd,
.flavor-menu-selection,
.HowToPay,
.NotifyMessage,
.product-image-carousel-container .primary-image-carousel-container .primary-image-carousel .slick-slide div,
.product-options .attribute .option-button,
.product-options .attribute .variation-button,
.product-variations .attribute .option-button,
.product-variations .attribute .variation-button,
.productView-options .form-field>.form-checkbox+.form-label,
.productView-options .form-field>.form-radio+.form-label,
.pt_account #secondary nav,
.pt_content.content-article aside,
.pt_customer-service #secondary nav,
.pt_error #secondary nav,
.pt_order #secondary nav,
.pt_store-locator #secondary nav,
.pt_wish-list #secondary nav,
.QuickSearch,
.QuickSearchResult .QuickSearchResultImage,
.SharePublicWishList,
.TabNav li a,
.ui-tabs .ui-tabs-nav li,
.ui-tabs .ui-tabs-nav li.ui-state-active,
div.selector,
div.uploader,
hr,
input.Textbox,
input[type="password"],
input[type="text"],
select,
textarea,
ul.tabs li {
	border: none!important;
}

table.CartContent,
table.CartContents,
table.General {
	border-collapse: separate!important;
}

.navPages ul {
	float: none!important;
}

.table-bordered td {
	border: 1px solid #fff!important;
}

/*___ Fonts ________________________________________________________________________*/

.btn,
.button,
.card-title>a,
.featured-products .title,
.form-label--alternate,
.form-label--inlineSmall small,
.label,
.productView-info-name,
.pt_content.content-article article>.intro p,
.ui-tabs .ui-tabs-nav li a,
body,
h1,
h2,
h3,
h4,
h5,
h6 {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.form-label--alternate,
.product-tile .product-name,
.productView-info-name,
.pt_content.content-article article>.intro p,
.pt_content.content-article aside li,
.ui-tabs .ui-tabs-nav li a,
h2 a {
	font-size: 25px!important;
}

.account-product-title,
.table-bordered td,
p,
small,
span {
	font-size: 17px!important;
}

#dialog p,
.button,
a,
ul {
	font-size: 15px!important;
	line-height: 27px!important;
}

.form-label--inlineSmall small {
	font-size: 14px!important;
	line-height: 19px!important;
}

body {
	font-size: 1rem!important;
}

/*___ Remove Items _________________________________________________________________*/

selector {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.body .home .homepage-buckets {
	bottom: -13rem!important;
}

.container {
	top: 8rem!important;
}

.header-logo,
.navPages-container {
	top: -8rem!important;
}

.navUser {
	top: -9rem!important;
}

.mbv-clear-fix ul {
	margin-left: 3rem!important;
}

.card-figcaption-body>:first-child {
	margin-top: 40rem!important;
}

/*___ Margins ______________________________________________________________________*/

#secondary,
.account-listItem,
.CartContents tfoot .CheckoutRedeemCoupon,
.ExpressCheckoutBlock,
.padder,
.page-content,
.page-sidebar,
.primary-content,
.productCarousel-slide,
.productGrid .product {
	margin: 12px!important;
}

.pt_content.content-article article>.intro {
	margin: -20px auto 0!important;
}

.pt_content.content-article .primary-content ul,
.pt_content.content-article .primary-content ol,
.pt_content.content-article .primary-content p {
	margin: 0 0 3px!important;
}

#section1 .primary-content a {
	padding: 1rem 0 23px!important;
}

/*___ Paddings _____________________________________________________________________*/

#secondary,
.account-listItem,
.CartContents tfoot .CheckoutRedeemCoupon,
.ExpressCheckoutBlock,
.featured-content ul li,
.level-2,
.navPage-subMenu-item>.navPage-subMenu-action,
.primary-content,
.productCarousel-slide,
.productGrid .product,
.productView-options .form-field {
	padding: 12px!important;
}

.cart-action-continue-shopping button,
.table-bordered td {
	padding: 9px!important;
}

/*___ Heights ______________________________________________________________________*/

.productGrid .product {
	height: 32rem!important;
}

/*___ Widths _______________________________________________________________________*/

.body .home .homepage-buckets,
.cart-recommendations .product-listing,
.center-content,
.container,
.featured-products .tabs,
.navPages,
.navPages .navPage-subMenu-list,
.product-breadcrumbs,
.product-description-container .product-description-columns,
.product-recommendations-container .product-recommendations-details,
.product-reviews-container .product-reviews-details,
.pt_content.content-article .article-main-content,
.pt_content.content-article .page-links {
	max-width: none!important;
}

.pt_content.content-article article>.intro {
	width: 92%!important;
}

.primary-content {
	width: 77%!important;
}

.pt_account .primary-content,
.pt_customer-service .primary-content,
.pt_error .primary-content,
.pt_order .primary-content,
.pt_store-locator .primary-content,
.pt_wish-list .primary-content {
	width: 72%!important;
}

.productCarousel-slide {
	width: 16%!important;
}

.navPage-subMenu.is-open {
	width: 60rem!important;
}

.page-sidebar {
	width: 22%!important;
}
}

