css:
@-moz-document domain(mokum.place) {

    body {
    font-family: 'Ubuntu', sans-serif !important;
}
.sidebar .box-body,
.content .box-body,
.content .box-footer,
.arrow-span,
.post,
.sidebar-emt,
.nodeAuth,
.sh-item,
.bootstrap-switch .bootstrap-switch-label,
.show-sidebar,
.phui-document-container,
.phui-document-content {
    background: none !important
}

a,
.expandable .expand-panel .expand-button,
.bem-sidebar .bem-live-selector .bootstrap-switch-handle-off,
.btn-link,
header .mobile-sidebar-toggle,
.post .read-more,
.phui-property-list-key,
.phui-crumbs-view a.phui-crumb-view,
.phui-crumbs-view a.phui-crumbs-action,
.user-card .user-card-actions a {
    color: #716184 !important
}

body,
.box-header,
.box-header-empty,
.box-header-timeline,
.box-header-friends,
.box-header-groups,
.box-header-search,
.post .post-body,
.post .post-top .post-text,
.post .post-body .post-text,
.Select-value-label-standalone,
.Select--multi .Select-value,
.profile .description,
.btn-default,
.open > .btn-default.dropdown-toggle,
.user-profile .profile-description,
.post-cont,
h3,
.sh-selected a,
.form-control,
label,
.bem-sidebar .bem-live-selector .bootstrap-switch-handle-on,
.bem-post__likes,
.alert-warning,
.bem-sidebar-menu__title,
.bem-sidebar-userinfo__display-name,
.Select-control .Select-value .Select-value-label,
.bem-post__posted-to_feeds,
input,
button,
select,
textarea,
.Linkify,
.bem-post__comment-text,
.alert,
.phui-property-list-value,
.phui-header-header,
.phui-header-subheader,
.phui-header-subheader .policy-link,
.phui-icon-view.bluegrey,
.phui-document-view-pro .phui-document-toc-header,
.phui-icon-view,
pre,
.phui-crumbs-view,
.phui-basic-nav .phabricator-side-menu .phui-list-item-type-label,
.card-body .display-name,
.user-card .username,
.user-card .feed-status,
.user-card .relationship-status,
.user-card .user-card-actions {
    color: #535353 !important
}

.post,
.profile,
.box-header,
.box-header-empty,
.box-header-timeline,
.box-header-friends,
.box-header-groups,
.box-header-search,
.create-post,
.profile-controls,
.sb-emt-title,
.pagetitle,
.settings-head div,
.bem-page__post,
.table td,
.table th,
hr,
.avatar img,
.post .post-userpic,
.image-attachments .attachment,
.new-post-feed,
.removeable-item,
.bem-sidebar-menu__divider,
.bem-page__phone-navigation {
    border-color: #333 !important;
    font-family: 'Ubuntu', sans-serif !important;
    font-size: 1.5rem;
    
}

.post-editor .post-textarea,
.comment .comment-textarea,
.search-form .search-input,
.Select-value-label-standalone,
.Select--multi .Select-value,
.Select-control,
.search-form .search-button,
.image-attachments .image-attachment-link,
.btn-default,
.open > .btn-default.dropdown-toggle,
.sidebar a.bookmarklet-button,
.form-control,
.edit-txt-area,
.input-group-addon,
input:not([type="image"]),
button:not(.pswp__button):not(.ril__toolbarItemChild):not(.ril__navButtons),
select,
textarea,
.bootstrap-switch,
.bem-sidebar .bem-live-selector .bootstrap-switch-handle-on,
.bem-sidebar .bem-live-selector .bootstrap-switch-handle-off,
.bg-warning,
.alert-warning,
header .mobile-sidebar-toggle,
.comment.targeted-comment,
.alert,
.actions-panel,
.actions-panel .mention-actions,
.phui-crumbs-view,
.phui-theme-indigo .phabricator-main-menu-background,
hr,
.policy-header-callout,
.phui-header-subheader .phui-tag-core,
button.grey,
input[type="submit"].grey,
a.grey,
a.grey:visited,
button.simple,
input[type="submit"].simple,
a.simple,
a.simple:visited,
.aphront-dialog-view,
.aphront-dialog-body,
.aphront-dialog-tail,
.phuix-dropdown-menu,
.phui-document-view-pro .phui-document-toc-list,
.phui-box-border,
pre,
.auth-account-view,
.hovercard,
.comment-likes .comment-likes-list,
.user-card,
.user-card .user-card-info,
.user-card .user-card-actions,
.userpic {
    border-bottom: 1px dashed #959595 !important;
}
textarea {
    background: #cecece;
}
.btn-default {
    background: #cecece;
}
.comment.highlighted,
.table-striped tbody tr:nth-of-type(2n) {
    background: #a8a8a8 !important
}

.comment.highlighted::before,
.phui-info-severity-notice,
.subscribe-controls {
    border-color: #444 !important
}

.comment:hover .reference-arrow,
.comment.highlight-from-url,
.phui-info-view,
.table-striped tbody tr:nth-of-type(2n+1) {
    background: #bdbdbd !important;
    color: #424242 !important
}

.comment.highlight-from-url::before,
.post::after {
    border-color: #333 !important
}

.comment:hover .reference-arrow:hover,
.phui-policy-section-view-header,
.phabricator-remarkup tt.remarkup-monospaced {
    background: #444 !important;
    color: #bbb !important
}

.phui-policy-section-view-header,
.phui-header-shell,
.phui-object-box .phui-oi-list-view .phui-oi-frame,
.application-search-view .application-search-results .phui-profile-header {
    border-color: #444 !important
}

.btn-success,
.phabricator-remarkup-embed-image {
    background: #666 !important;
    color: #bbb !important;
    border: 0 !important
}

.search-form .search-input {
    margin-top: 0 !important;
    padding: 2px 4px !important
}

.link-preview .folder-container .content-wrapper.folded::after {
    background: -webkit-linear-gradient(rgba(34, 34, 34, 0) 0%, rgba(34, 34, 34, 0.2) 25%, rgba(34, 34, 34, 0.8) 75%, #222 100%) !important;
    background: linear-gradient(rgba(34, 34, 34, 0) 0%, rgba(34, 34, 34, 0.2) 25%, rgba(34, 34, 34, 0.8) 75%, #222 100%) !important
}

.fold-comments-sticky {
    background: rgba(34, 34, 34, 0.9) !important;
    box-shadow: 0 0 4px 8px rgba(34, 34, 34, 0.9) !important
}

.preview-expand {
    padding-bottom: 8px !important
}

.comment-icon i.fa-inverse {
    display: none !important
}

.likes .likes-icon .icon-heart,
.comment .comment-icon.comment-icon-special .icon-comment,
.comment .comment-icon .icon-comment,
.comment .comment-icon .icon-comment-plus,
.comment .comment-likes.clikes-zero .clikes-icon .icon-heart,
.comment .comment-likes .clikes-icon .icon-heart {
    fill: #222 !important
}

.likes-smile .icon {
    color: #222 !important
}

.actions-overlay {
    background-color: rgba(34, 34, 34, 0.8) !important
}

body,
.sidebar,
.expandable .expand-panel,
.phui-oi,
.phui-basic-nav .phabricator-side-menu,
.aphront-form-input input,
.aphront-form-control input,
.phui-two-column-view .phui-two-column-header,
.auth-account-view {
    background: #c3c3c3 !important
}

.bem-post__posted-to {

    color: #8C8C8C !important;
}
.Select-control {
    background-color: #cecece;
}
.Select-placeholder,
.Select--single > .Select-control .Select-value {
    background: transparent;
    bottom: 0;
    color: #656565 !important;
}
.bem-signin-block_header {
    padding: 10px;
    border: 1px hidden;
    border-radius: 2px;
    background-color: #d2d2d2;
    margin-right: 0;
}
.bem-sidebar-userinfo,
.bem-sidebar__sidebar-menu {
    border: 1px hidden;
    border-radius: 2px;
    padding: 5px 5px 20px 5px;
    background-color: #d2d2d2;
}
.bem-sidebar-menu__item {
    margin: 0;
}
.bem-post.bem-page__post.js-post-container {
    border: 1px hidden;
    border-radius: 2px;
    padding: 10px;
    margin-bottom: 10px;
    background-color: #d2d2d2;
}
.bem-page__post {
    border-color: #d2d2d2 !important;
}
.search-form .search-button:hover,
.search-form .search-button:focus,
.search-form .search-button:active,
.btn-default:hover,
.btn-default:focus,
.btn-default.focus,
.btn-default:active,
.btn-default.active,
.open > .btn-default.dropdown-toggle,
.sh-selected,
.new-post-feed,
.removeable-item {
    background: #dfdfdf;
}
.bem-central-block {
    background-color: transparent;
}
.bootstrap-switch-handle-on.bootstrap-switch-primary,
.bootstrap-switch-handle-off.bootstrap-switch-default {
    background-color: #d2d2d2 !important;
}
small {
    display: none !important;
}
}
