css:
@-moz-document domain("smarden.org") {
html {
	background: #181616;
	width: auto;
	max-width: inherit;
}

body {
	background-color: #252222;
	color: #adaaaa;
	padding: calc(1vh + 1vw);
}

body > h1:first-child {
	margin-top: 0;
}

a:link {
	color: #8679ff;
	text-decoration: none;
}
a:hover,
a:active {
	color: #8679ff;
	text-decoration: underline;
}
a:visited {
	color: #5447C5;
}

H1, H2, H3, H4, H5, H6 {
	font-weight: bold;
	color: #cfcccc;
	border-bottom: 1px solid #fff2;
}

blockquote, pre {
	margin-left: calc(-1vh - 1vw);
	margin-right: calc(-1vh - 1vw);
	background: #1d1a1a;
	padding: 1rem calc(1vh + 1vw);
	border-bottom: 1px solid #fff2;
	border-top: 1px solid #0002;
	box-shadow: 10px 0 5px #0002 inset;
}

tt {
	display: inline-block;
	background: #0005;
	padding: 2px 4px;
	border-radius: 3px;
	border-bottom: 1px solid #fff2;
	border-top: 1px solid #0002;
}

hr {
	border: none;
	display: block;
	height: 1px;
	margin: 2em auto;
	
	background-image: linear-gradient(to right, rgba(255,255,255,0) 0%,rgba(255,255,255,.5) 50%,rgba(255,255,255,0) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

input[type="text"] {
	width: 100%;
	background-color: #ffffff15;
	border-width: 0 0 2px 0;
	border-color: #ffffff40;
	padding: 5px;
	box-sizing: border-box;
}
input[type="text"]:focus {
	background-color: #ffffff30;
	border-color: #8679ff;
}

input[type="submit"] {
	width: 100%;
	background: #555;
	border: none;
	padding: 5px;
	box-sizing: border-box;
}
input[type="submit"]:hover,
input[type="submit"]:active {
	background-color: #666;
}

@media screen and (min-width: 1280px)
{
	body {
		width: 55%;
		margin: 10vh auto;
		box-shadow: 0 0 calc(1vh + 1vw) #0008;
		min-width: 1024px;
	}
}
}
