css:
@-moz-document domain("hotcopper.com.au") {

/*New HC*/
body {
    background-color: #FFFCF4;
}

a {
	color: #1666ca;
}

a.is-black-text.is-extra-small {
    font-size: 0 !important;
}

a.is-black-text.is-extra-small:after {
    content: '☰';
	width: 18px;
	height: 18px;
	font-size: 0.9rem;
	font-weight: bold;  
	line-height: 18px;
	border-radius: 50%;
	color: #022a91;
	text-align: center;
    display: inline-block;
	background-color: #c1eaf2;
}

.post-list-component .outer .table .thread-text {	
    font-size: 0 !important;
	width: 18px !important;
	height: 18px !important;
	color: #022a91 !important;
	border-radius: 50% !important;
	background-color: #c1eaf2 !important;
}

.post-list-component .outer .table .thread-text:after {
    content: '☰' !important;
	color: #022a91 !important;
	font-size: 0.9rem !important;
	background-color: #c1eaf2 !important;
}

.post-list-component .outer .table .poster-td.no-overflow {    
    text-align: right;
}

.has-black-link a {
	color: #d36404 !important;
}

header .row-top .nav-top {
	background-color: #231F20;
}

header .row-top .nav-top .nav-right .logged-in-outer {
	padding-left: 10px !important;
	padding-right: 10px !important;
}

header .nav-top .nav-right .logged-in-outer .notifications-btn .panel {
	left: calc(50% - 105px);
}

header .nav-top .nav-right .logged-in-outer .notifications-btn .panel .up-icon {
	left: 95px;
}

header .row-search .hc-search {
	margin-left: 5px;
}

header .row-search.search-fixed .hc-search .field .is-primary {
	padding: 0 0.8em;
	height: 34px;
}

header .row-search .hc-search .is-primary {
	padding: 1.0em 0.8em;
	height: 44px;
}

header .row-search .hc-search .pos-vert-left {
	top: 0 !important;
}

header .row-search .mob-search .menu {
	margin-left: 4px;
	margin-right: 4px;
}

header .row-top .nav-top .nav-right .logged-in-outer .watchlist-btn .watchlist-widget {
	left: 0px;
    z-index: 9801;
}

header .nav-top .nav-right .logged-in-outer .my-account-btn .panel {
	z-index: 9801;
}

header .row-search .level-right {
	margin-right: 15px;
}

@media screen and (min-width: 980px) {
    .post-list-component .outer .controls .pagination-col .pagination {
        float: right;
    }
}

.hc-content .column.left {
	width: 100%;
}

main.home-page .hc-content .column.left .top-rated-component {
	margin-right: 0;
}

.post-list-component .list-header h1 {
	margin-left: 5px;
	margin-right: 15px;
}

.post-list-component .filter-fav-tags-field {
	margin-right: 5px;
}

.container {
	max-width: 100%;
	width: 100%;
}

.hc-content .column.left {
	margin-right: 0;
}

div.controls.columns {
    background-color: #FDF3D7 !important;    
    opacity: 0.6;
}

th {	
    background-color: #FFFFFE !important;
}

tr, .alt-tr {	
    background-color: #FFFCF4 !important;
}

tr:nth-of-type(odd) {	
    background-color: #FDF3D7 !important;
}

.post-list-component .new-post-span .new-post-btn {
	background-color: #fff;
}

.post-list-component {
	margin-right: 0;
}

.post-list-component .outer .table tbody td {
	margin: 0 10px 5px 10px;
    padding-left: 0;
    padding-right: 0;
}

.post-list-component .outer .table tbody td.stock-pill-td {
	padding-right: 0;
	min-width: 50px;
}

.stock-pill a {
	color: #FFFFFF !important;
    font-size: 1.3rem;
	background-color: #F77214;
	border-radius: 3px;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
}

.stock-pill {	
    padding-left: 7px;
}

.time-td {
    padding-left: 57px !important;
}

.speech-icon,
.speech-icon + em {
	color: #3188db;
}

.eye-icon,
.eye-icon + em {
	color: #e431cd;
}

.thumbsup-icon,
.thumbsup-icon + em {
	color: #24b513;
}

.calendar-icon,
.calendar-icon + em {
	color: #cf412b;
}

.post-list-component .outer .table tbody .forum-td {
    padding-left: 10px !important;
    min-width: 10px;
}

.post-list-component .outer .table tbody td.title-td {
	padding-left: 7px;
}

.post-list-component .outer .table .mob-detail-tr td {
	padding-left: 54px !important;
    padding-right: 4px;
}

.post-list-component .outer .table tbody td.deleted a {
	color: #514E4C;
}

.post-list-component .outer .table tbody .last-post-td {
	padding-right: 10px;
}

.list-detail-mob .icon-td {
	width: 55px;
}

td.icon-td:last-of-type {
    padding-right: 10px !important;
}

main.thread-page .hc-content .left {
	max-width: 100%;
}

main.company-page .hc-content .left {
	max-width: 100%;
}

main.thread-page .hc-content .left .thread-content .thread-full-paywall #quick-reply-form {
	margin-right: 0px;
	margin-left: 0px;
}

main.thread-page .hc-content .left .thread-content .thread-full-paywall .message-list .message-post .message-controls {
	margin-left: 0;
	margin-right: 0;
}

}
