}


@-moz-document domain("item.taobao.com") {
.WarningMsg, #RecommendedItems, #PaymentMethod, #GuestBook, .AlipayWarningMsg, #UserAgreement, .safety, .bonus-action, .safety-service, #SummaryInfo, #AssocBrowse, .item-payment-way, .item-afterservices, #guestbook{display:none !important;}

/*100311 Style*/
.shop-summary, #detailtoolbar, .payway-info, #related-items, #other-info, .exemption-clause, #payway-info{display:none !important;}
.eshop #hd .grid-c, .shop-header{height:30px !important;background-image:none !important;;background-color:#fff !important;}
}

@-moz-document domain("search1.taobao.com") {
#p4p-mask, .p4p-tabbed, #SideBar, .nav-main, #list-free-content{display:none !important}
#ListSubCategory{margin-right:0 !important}
.filter-box{width:auto}
}

@-moz-document domain("list.taobao.com"),domain("search1.taobao.com") {
#p4p-mask, .p4p-tabbed, #SideBar, .nav-main{display:none !important}
#MainContainer, #ListPathLinks, #ListPathOptions, #J_MoreFilterOptions, #ListView{margin-right:0 !important;}
#page{padding:0 2% !important}
#page, #ListView, #filter, .list-content, .list-view-enlarge{width:auto !important;}
.mall-recom, .mall-recom .skin{background-image:none !important;}
.list-view-enlarge li.list-item{padding:0 3px !important;}
.list-view-enlarge .attribute{opacity:0.5}
.list-view-enlarge .other,.summary{left:0 !important}
}

@-moz-document domain("search.taobao.com"), domain("ss.taobao.com") {
.col-sub, .p4p-tabbed{display:none !important}
.grid-c2f .main-wrap{margin-right:0 !important}
.parent-cat, .parent-cat dl, .parent-cat dd, .parent-cat ul, .toolbarWrapper, .toolbar, .cat-panel table, .selected-items, .all-cat, .all-cat dl, .list-content,
.comptoolbar .toolbar, .list-compare #J_mall{width:auto !important}
.list-view-enlarge li.list-item{padding:10px 2px !important;width:233px !important;overflow:hidden !important}
.toolbar{float:none}
.list-compare #J_mall .list-item .item-wrap{padding:10px 2px !important}
#filterForm div.advanced{width:850px !important}
#page{width:auto !important;padding:0 2% !important}
.list-view{width:760px !important;margin:0 auto !important;}
#page .list-view-enlarge h3.summary{width:220px;left:5px}
#page .list-view-enlarge p.extend{right:15px;}
.list-view-enlarge ul.attribute .legend2,.list-view-enlarge p.extend{opacity:0.5}
#page .list-view-enlarge ul.attribute{width:222px;}
#page .h, .H{background-color:#ffff99;color:#0063DC !important;}
#filterForm button.submit{margin-top:2px !important;}
/*Top Three*/
#J_mall{float:left}
.list-view-enlarge li.list-item{height:355px !important;}
}
