css:
@-moz-document domain("www.tu-ilmenau.de") {
* {
    background: #111;
    color: #ddd;
}

body {
    background: #222;
}

.cc_banner_wrapper, .cc_banner {
    display: none;
}

#ansprechpartner, strong {
    background: inherit;
}

#head_first, #servicenavigation {
    background: #333;
    color: #eee;
}

input, #common_button, #headerphoto, .mainarea, #navileft ul li a {
    background: #222;
    background-image: none;
    color: #fff;
}

#note1_active {
    background: inherit;
    border: none;
}

#cssitem_ori .wrap_head_second {
    background: #44f;
}

#cssitem_ori #navileft ul li a.active {
    background: #444 !important;
}

#cssitem_ori #navileft ul li ul li a {
    background: #555;
    border-left-color: #88f;
}

#cssitem_ori #navileft ul li ul li a.active {
    background: #666 !important;
    color: #88f !important;
}

.greystripe, #head_stripe_bottom, #navileft {
    background: #444;
    color: #eee;
}

.fak_small_head #head_left a {
    color: #44f;
}

#wrap_endofsite {
    border: none;
}

div.pagetitle {
    background: #44f;
}

#cssitem_ori .no-border .title h1, #cssitem_ori h3 {
    color: #44f !important;
}

#cssitem_ori #navileft ul li ul li span.active {
    color: #44f !important;
    background: #555 !important;
    border-left-color: #44f;
}

#cssitem_ori #navileft ul li ul li a {
    color: #eee;
}

div.pagetitle h2 {
    background: inherit;
    color: #eee;
}

.ui-tabs .ui-tabs-panel {
    background: inherit;
}

.ui-widget-header {
    border-color: #44f !important;
}

.ui-widget-content .ui-state-active {
    background: #44f;
}

.ui-tabs .ui-tabs-nav li a, .ui-tabs .ui-tabs-nav li a span {
    background: inherit;
}
}
