css:
@-moz-document domain("magister.net") {
.menu-host * {
    background-color: rgba(0, 0, 0, 0);
}
.menu-host {
    background-image: url("https://i.postimg.cc/0jXp1XBD/untitled.png");
    background-size: cover;
}
.appbar-host {
    background-image: url("https://i.postimg.cc/0jXp1XBD/untitled.png");
    background-size: cover;
    border-right: 1px solid rgba(255, 255, 255, .2);
}
.main-menu>li>a:hover {
    background-color: rgba(255, 255, 255, .3);
}
.main-menu>li.active>a {
    background-color: #ffffff;
}
.main-menu>li.active>a>* {
    filter: brightness(0%);
}
.main-menu>li.active>a>div.icon-stack>i.fa-circle {
    position: absolute;
    left: -10px;
}
.appbar>div>a {
    background-color: #ffffff;
}
.appbar>div>a>i {
    filter: brightness(0%);
}
.fa-layers-counter {
    background-color: #ff0000;
}
div.popup-menu>ul>li.submenu>a:hover {
    background-color: rgba(255, 255, 255, .3);
    border-radius: 6px;
}
div.popup-menu>ul>li.submenu>a {
    padding: 7px;
}
}
