css:
@-moz-document domain("xda-developers.com") {
.tb_widget_recent_list,
.tb_widget_posts_big,
.widget_title,
.xda_suggested_apps,
.w-social,
.et_social_icons_container,
.widget-1,
.xda-post-related,
.et_social_inline,
.author_box,
.hosted_by,
.et_social_mobile_on,
.f-links,
.footer {
    display: none !important;
}

/* reduce width, bigger font size for better reading. */
.post-text {
    width: 60%;
    margin: auto;
    font-size: 1.2em !important;
    line-height:180%
}

/* annoying news please get out of my face. */
#stickyNav {
        opacity: 0 !important;
    transition: 1500ms !important;
}
#stickyNav:hover {
    opacity: 1 !important;
    transition: 200ms !important;
}

/*eliminate the social media icons*/
#header_social_wrapper {
    display: none !important;
}

/*eliminate the footer at the bottom*/
#footer {
    display: none !important;
}
}
