css:
@-moz-document domain("web.telegram.org") {

/* Dark Telegram Theme by At-M */
/* http://userstyles.org/users/62569 */
/* Author: At-M */
/* Contact: atmsupload@gmail.com */


  :root {
    --dark:    #272C35;
    --darker:  #1F232A;
    --light:   #D1D1D1;
    --lighter: #E9E9E9;
    --accent:  #9013fe;
    --icon:    #E1E1E1;
    --shadow:  rgba(0,0,0,0.10);
    --mred:    #A3525B;
    --mgreen:  #70A352;
    --mblue:   #527AA3;
    --bg: url(https://media.discordapp.net/attachments/310799529161129984/417408747812552715/zilesanamin.jpg);
    --chatbg: url(https://i.imgur.com/AzleJ7c.png);
      
    --emojiOpacity: 0.65;
  }

  /* "Privacy mode." */
    /* Blurry mode for contacts. */
  .im_dialog_wrap, .im_message_photo_thumb {
    filter: blur(10px); }
  /* On event. */
  .im_dialog_wrap.active, .im_message_photo_thumb:hover,  .im_dialog_wrap:hover {
    filter: blur(0px); }

/* Widescreen */
.im_send_form, .im_send_panel_wrap, .im_message_wrap {
    max-width: 1000px;
}
.tg_head_split {
    margin-top: 10px;
}
.im_page_wrap, .tg_head_split {
    max-width: 1396px;
    border: 1px;
    border-color: rgba(31,35,42,1);
}

.im_history_scrollable_wrap{
    background-image: var(--chatbg);
    background-color: #272C35;
}
.im_message_text{
    background-color: #1F232A;
    border: 1px #000;
    padding: 10px;
    border-radius: 0px 10px 10px 10px;
}
a.pull-right, #notify_sound{
    display: none;
}
body, .modal-content{
    background-color: black !important;
    color:#cccccc !important;
        
        background-image: url("https://media.discordapp.net/attachments/310799529161129984/417408747812552715/zilesanamin.jpg");
    background-size:cover;
    }
    a.tg_checkbox {
        color: #ccc !important;
    }

    .im_page_wrap{ /*background color of contentwrapper under header*/
        background-color: #1d1d1d !important;
        border-left: 1px solid var(--dark) !important;
        border-right: 1px solid var(--dark) !important;
        border-bottom: 1px solid var(--dark) !important;
        box-shadow: none;
    }

    .im_dialog_peer { /*peer nickname unselected*/
        color:#cccccc !important;
    }
    
    .contacts_modal_members_list a.contacts_modal_contact { /*nicknames in contactlist via hamburger menu*/
       color: #cccccc;
    }


    .active a.im_dialog { /*selected peer */
    background-color: var(--dark) !important;
    }

    .tg_head_split, .md_modal_head{ /*page header block*/
    background-color: var(--dark) !important;
    }

    .dropdown.open .tg_head_btn, .tg_head_btn:hover,  .im_bottom_panel_wrap { 
        background: var(--dark) !important;
    }
.im_dialogs_panel, .im_dialogs_scrollable_wrap, .tg_head_logo_wrap{
    background-color: var(--darker);
}

    .form-control { /*input field*/
    background-color: #272C35;
        border-radius: 5px;
    color: #ffffff !important;
        border-color: transparent;
    }

    .im_dialogs_col_wrap { /*contactlist right border*/
        border-right: 2px solid var(--dark); 
    }

    .im_dialogs_col .nano > .nano-pane > .nano-slider { /*scrollbarbutton when hover*/
        background-color: var(--dark) !important;
    }

    .tg_head_split {
        background-color: #000000;
    }    
  .im_submit {
        color: var(--dark);
    }
    
    .im_message_mymention {
    background: var(--darker);
    }

  .im_message_focus .im_message_outer_wrap {
    background-color: var(--dark);
    }
    
    .im_message_selected .im_message_outer_wrap {
    background: var(--dark);
    }
    
    a:hover {
      background-color: var(--dark) !important;
    }
    .im_message_unread_split {
    background: var(--dark);
    }
    
    @keyframes im_message_focus_fade{
0% { /*soft transaction*/
    background-color: #2d2d2d;
}
    }
    .btn-primary {
    background-color: var(--dark);
    }
   
    .im_history_select_active  .im_message_outer_wrap:hover {
        background: var(--dark);
    }
    .im_dialog_chat_from_wrap,  .im_dialog_message_service {
    color: #000000;
    }
   .composer_rich_textarea, .composer_textarea {
        box-shadow: 0px 3px 1px #ffffff;
    }
    .composer_rich_textarea:focus {
        box-shadow: 0px 3px 1px #ffffff !important;
    }
    
    .tg_head_logo_dropdown .dropdown-menu > li > a {
        color: #ffffff;
    }
    
    .btn-primary.disabled, .btn-primary[disabled] {
    background: var(--dark) none repeat scroll 0% 0%;
}
   
        /*emoji*/

    .composer_emoji_tooltip, .icon-tooltip-tail, .dropdown-menu  {
        background-color: var(--dark) !important;
        border-color: #5d5d5d;
    }
}
