/* ==UserStyle==
@name           zive.cz - 10/17/2018, 7:59:38 PM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("zive.cz") {
    :root {
        --bg-color-background: #23232d;
        --bg-color-lighter: #2c2c36;
        --bg-color-lightest: #3b3b4b;
        --text-color-primary: #dfdfdf;
    }

    .tm-r1 {
        background: var(--bg-color-lighter, #2c2c36) !important;
        color: var(--text-color-primary, #dfdfdf);
    }

    .tm-r2 {
        background: var(--bg-color-lighter, #2c2c36) !important;
        color: var(--text-color-primary, #dfdfdf);
    }

    .tm-r2 .cs-cnt > span,
    .tm-r2 .cs-cnt > ul {
        background: var(--bg-color-lighter, #2c2c36) !important;
        color: var(--text-color-primary, #dfdfdf);
    }

    .tm-r1 .cs-cnt {
        background: var(--bg-color-lighter, #2c2c36) !important;
    }

    .tm-r2 ul.perma-links li a {
        background-color: var(--bg-color-lighter, #2c2c36);
    }

    .tm-r2 .cs-menu-cnt {
        background: var(--bg-color-lighter, #2c2c36) !important;
        color: var(--text-color-primary, #dfdfdf);
    }

    .tm-r2 .war-mr-wrapper a {
        color: var(--text-color-primary, #dfdfdf);
    }

    body > form > div > div {
        background: var(--bg-color-lighter, #2c2c36) !important;
        color: var(--text-color-primary, #dfdfdf);
    }

    body > form > div:nth-child(6) {
        display: none;
    }

    body > form > div:nth-child(8) {
        display: none;
    }

    div.sky-wrapper > div > div:nth-child(5) {
        display: none;
    }

    div.branding-wrapper > div:nth-child(8) {
        display: none;
    }

    div.branding-wrapper > div:nth-child(9) {
        display: none;
    }

    div.branding-wrapper > div:nth-child(10) {
        display: none;
    }

    div.branding-wrapper > div:nth-child(11) {
        display: none;
    }

    div[data-contentexchange-widget] {
        display: none;
    }

    div[id^="sklikReklama"] {
        display: none;
    }

    div[class^="L1"] {
        display: none;
    }

    .sky-wrapper .bx-sky {
        display: none;
    }

    .bx-commercial {
        display: none;
    }

    .bx-rectangle-article {
        display: none;
    }

    .bx-livecompanies {
        display: none;
    }

    .bx-leaderboard {
        display: none;
    }

    .bx-sky {
        display: none;
    }

    .body {
        color: var(--text-color-primary, #dfdfdf);
    }

    body > form > div > div:nth-child(6) {
        background: var(--bg-color-lighter, #2c2c36) !important;
        color: var(--text-color-primary, #dfdfdf);
    }

    .cs-cnt-main {
        background-color: var(--bg-color-background, #23232d);
    }

    .cs-cnt-cl {
        background-color: var(--bg-color-background, #23232d);
    }

    .ar-detail .ar-content {
        color: var(--text-color-primary, #dfdfdf);
    }

    .bx-cnt ul a {
        color: var(--text-color-primary, #dfdfdf);
    }

    .bx-cnt {
        color: var(--text-color-primary, #dfdfdf);
    }

    .ar-chapters a.ar-btn,
    .ar-chapters .ar-btn-comments,
    .ar-chapters .ar-btn-comments a {
        color: var(--text-color-primary, #dfdfdf);
    }

    .ar-content .ar-tags {
        color: var(--text-color-primary, #dfdfdf) !important;
    }

    .ar-content .ar-tags a {
        color: var(--text-color-primary, #dfdfdf) !important;
    }

    #pfm-under-article {
        display: none;
    }

    .ar-list .ar-row .ar-txt h2 a {
        color: var(--text-color-primary, #dfdfdf) !important;
    }

    .ar-detail .ar-society-networks .ar-society-networks-wrapper {
        background-color: var(--bg-color-background, #23232d);
    }

    .ar-detail .ar-society-networks .ar-info .ar-date {
        color: var(--text-color-primary, #dfdfdf) !important;
    }

    .forum-row-wrapper .forum-head {
        background: var(--bg-color-lightest, #3b3b4b) !important;
        border-top: 4px solid var(--bg-color-lightest, #3b3b4b);
        border-top-color: var(--bg-color-lightest, #3b3b4b);
        border-top-width: 4px;
    }

    .forum-row-wrapper.readed .forum-head,
    .forum-row-wrapper.readed .forum-text {
        color: var(--text-color-primary, #dfdfdf) !important;
    }

    .cs-cnt-main {
        background: var(--bg-color-background, #23232d);
        background-color: var(--bg-color-background, #23232d);
    }
}
