css:
@-moz-document regexp("https?://4pda.(ru|to)/forum/.*") {
/*!
[DoC] Darker 4PDA - Color overrides
by Dark Preacher - dark[at]darklab.ru
 */
:root {
    /* Default font size */
    --fz: 16px;

    /* Text color: hue, saturation, lightness */
    --fg-h: 0;
    --fg-s: 0%;
    --fg-l: 95%;

    /* Background color: hue, saturation, lightness */
    --bg-h: 0;
    --bg-s: 0%;
    --bg-l: 5%;

    /* Accent color: hue, saturation, lightness */
    /* Green */
    --accent-h: 76;
    --accent-s: 50%;
    --accent-l: 48%;

    /* Orange */
    /* --accent-h : 29;
    --accent-s : 100%;
    --accent-l : 42%; */

    /* Red */
    /* --accent-h: 11;
    --accent-s: 100%;
    --accent-l: 61%; */

    /* Pale blue */
    /* --accent-h: 215;
    --accent-s: 32%;
    --accent-l: 43%; */

    /* Text color on accent background */
    --fg-accent: var(--bg);
}
}
