css:
@-moz-document domain('docs.feathersjs.com') {
/**
 * All .book prefixes are for versions prior to v4 (buzzard, auk, legacy).
 * All #app prefixes are for v4.
 */
html,
body {
    background-color: #0f1311;
}

.book .book-summary {
    background-color: #191f1e;
}

.book .book-body {
    background-color: #0f1311;
    color: #888888;
}

.book .book-body .page-wrapper .page-inner section.normal blockquote,
.book .book-body .page-wrapper .page-inner section.normal dl,
.book .book-body .page-wrapper .page-inner section.normal ol,
.book .book-body .page-wrapper .page-inner section.normal p,
.book .book-body .page-wrapper .page-inner section.normal table,
.book .book-body .page-wrapper .page-inner section.normal ul {
    color: #777777;
}

.book .book-summary ul.summary li a,
.book .book-summary ul.summary li span {
    color: #2f866b;
}


.book .book-summary nav ul.summary li a:hover,
.book .book-summary nav ul.summary li a:focus {
    color: #888888;
}

.book .versions-select select {
    background-color: #0d0e0e;
}

.book #book-search-input {
    background-color: #303332;
    color: #888888;
}

.book .markdown-section {
    color: #888888;
}


.book .markdown-section pre,
.book .markdown-section code {
    background-color: #000000;
}

.book .markdown-section table tr {
    background-color: #000000;
}

.book .markdown-section table tr:nth-child(2n) {
    background-color: #111111;
}

.book .markdown-section table td,
.book .markdown-section table th {
    border-color: #555555;
}

.book .markdown-section table th {
    background-color: #222222;
    color: #999999;
}

.book .book-body .page-wrapper .page-inner section.normal blockquote {
    border-color: #555555;
}

#app {
    color: #888888;
    background-color: #0f1311;
}

#app a {
    color: #ba4d9f;
}
#app .navbar .logo,
#app .content:not(.custom) img {
    filter: invert(1);
}
#app .navbar,
#app .navbar .links,
#app .sidebar {
    background-color: #191f1e;
    border-color: #333333;
}
#app .sidebar a:not(.active) {
    color: #888888;
}
#app .search-box input {
    background-color: #303332;
    color: #888888;
    border-color: #555555;
}
#app blockquote,
#app footer.page-edit blockquote,
#app footer.page-edit blockquote p {
    border-left-color: #8f3b7a;
    color: #929faa;
    background-color: #222;
}

#app div[class*=language-],
#app * > code {
    color: #5488ba;
    background: #000000;
    border: 1px solid #333333;
}
#app pre[class*=language-] code {
    border-width: 0;
}

#app .tabs-component-panels,
#app .tabs-component-tab {
    background-color: #272727;
    border-color: #3a3a3a;
}
#app .tabs-component-tab.is-active {
    border-bottom-color: #272727;
}
#app .tabs-component-tab:not(.is-active) {
    opacity: 0.8;
}
#app table tr {
    background-color: #000000;
}
#app table tr:nth-child(2n) {
    background-color: #171717;
}
#app table td,
#app table th {
    border-color: #555555
}
#app table th {
    background-color: #272727;
    color: #999999;
}
}
