.athing > td {
    border-top: 3px solid #f2f3f5;
}

.athing + tr .subtext {
  position: relative;
}

.athing + tr .subtext {
  /*border-bottom: 1px solid rgba(0, 0, 0, 0.05);*/
  padding-bottom: 15px;
}

.athing td {
  padding: 16px 0 0 0;
}

.athing .title:first-child {
  padding-left: 15px;
}

.athing .title a:link,
.athing .title a:visited {
    color: #000022;
    font-size: 15px;
}
.athing .title a:visited {
    color: #550077
}

.athing .title .rank {
    color: #ccc;
}

.athing .title .comhead, .athing .comhead a span {
    color: #ccc !important;
}

.subtext {
    color: #ccc;
    font-size: 14px;
    font-weight: normal;
}

.subtext .score {
    color: #999;
    font-size: 14px;
    font-weight: normal;
}

.subtext a:link,
.subtext a:visited {
    color: #aaa;
    font-size: 14px;
    font-weight: normal;
    text-decoration: none;
}

.subtext a:hover {
    color: #9ac;
    text-decoration: underline;
}

.comment-tree {
  background-color: #fff;
}

.comment-tree .athing td {
  padding: 5px 10px 5px 0;
}

.comment-tree .default .comhead {
  font-size: 14px !important;
  color: rgba(0,0,0,0.5);
}

.comment-tree .comment {
  font-size: 14px !important;
  color: rgba(0,0,0,0.5);
  line-height: 1.42em;
}

.comment-tree .comment .c00 {
    color: #34495e;
}

#hnmain table {
  width: 100%;
}

#hnmain form textarea {
  display: block;
  width: 100%;
  box-sizing: border-box;
  background: #fff;
  border: 1px solid rgba(0, 0, 0, 0.05);
  font-weight: 400;
  font-size: 14px;
  padding: 10px;
  outline: none;
}

#hnmain form input[type=submit]{
  border: solid 1px #F06D3C;
  height: 32px;
  background: transparent;
  border-radius: 32px;
  padding: 0 12px;
  color: #F06D3C;
  cursor: pointer;
}

.itemlist tr:last-child .title {
  padding: 10px 0;
}

.itemlist tr:last-child .title a {
  border: solid 1px #F06D3C;
  height: 32px;
  line-height: 32px;
  background: transparent;
  border-radius: 32px;
  padding: 0 12px;
  color: #F06D3C;
  display: inline-block;
}
td.ind {
  width: 0px;
}
