css:
@-moz-document url-prefix("https://togethertube.com/"), url-prefix("http://togethertube.com/") {
body, .wrapper .content-wrapper, .container-non-responsive, .navbar-static-top, a.list-group-item:hover {
    background:#252525;
}

.masthead .container-non-responsive {
    background:none !important;
}

::-webkit-scrollbar {
    width: 15px;
    background: #000;
}

::-webkit-scrollbar-thumb {
    background: #47A0C1;
}

a.link, a {
    color:#47A0C1;
}
a:hover {
    color:#72D9FF;
}

.navbar-default, ol.videoList>li.videoListEntry .header, .player-overlay-wrapper .player-overlay {
    background:#141414;
    border-color:#000;
}

.navbar-room-header .container-non-responsive, .footer {
    background:#141414 !important;
    border-color:#000;
}

.form-control, .list-group-item {
    background:#333;
    color:#f1f1f1;
    border-color:#000 !important;
}


ol.user-list>li:first-child, ol.user-list>li, #player #positionSlider, .slider, ol.videoList>li.videoListEntry .content, .panel-default, hr, .page-header, .table>thead>tr>th, .table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td, .table-permissions tbody td:first-child, .panel-footer, .nav-tabs>li>a:hover, ol.videoList>li.videoListEntry .content>.list-container:nth-child(3), .player-overlay-wrapper .player-overlay .player-overlay-textbox .video-blocked-countdown, .pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    border-color:#000;
}

.btn-success, .btn-success .fa, .btn-danger, .btn-danger .fa {
    color:#000 !important;
}

ol.user-list>li:nth-child(even), .panel, .table-striped>tbody>tr:nth-of-type(odd), .panel-footer, ol.videoList>li.videoListEntry .content>.list-container:nth-child(3) {
    background:rgba(0,0,0,0.4);
}
ol.user-list>li:nth-child(odd), ol.videoList>li.videoListEntry .content, .table-striped>tbody>tr:nth-of-type(even), .player-overlay-wrapper .player-overlay .player-overlay-textbox, .nav-tabs>li>a:hover, .player-overlay-wrapper .player-overlay .player-overlay-textbox .video-blocked-countdown {
    background:rgba(0,0,0,0.2);
}

.ng-binding, .col-xs-4 ol .fa, ol.videoList .fa, ol.videoList .ng-scope, body, p, .features-icon-box li p.description, .use-cases-icon-box li p.description, .panel-chat .fa, .dropdown-menu a.ng-binding, .navbar-default .navbar-text, .dropdown-menu>li>a {
    color:#A7A7A7;
}

.btn-remove-playlist-item:Hover .fa {
    color:#FFF !important;
}


.btn[disabled], .btn[disabled]:hover, .pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus, .list-group-item.disabled, .list-group-item.disabled:hover, .list-group-item.disabled:focus {
    background:#41454D !important;
    border-color:#000 !important;
    opacity:0.3 !important;
}

.dropdown-menu>.disabled>a, .dropdown-menu>.disabled>a:hover, .dropdown-menu>.disabled>a:focus, .dropdown-menu>.disabled .fa, .dropdown-menu>.disabled:hover .fa  {
    opacity:0.3;
    color:#f1f1f1 !important;
    background: none !important;
}

.features-icon-box li, .use-cases-icon-box li {
    background: rgba(219, 227, 255, .1);
}

.room-summary .panel-body h5 {
    color:#A6BDED;
}

.slider .bubble, .navbar-room-header .navbar-nav>li>a:hover, .btn-default:hover, .navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus, .dropdown-menu a.ng-binding:hover, .dropdown-toggle .badge, .dropdown-menu a.ng-binding .badge, .badge.ng-binding, .nav-tabs>li.active>a, .pagination>li>a, .pagination>li>span:hover, .pagination>li>a:hover {
    background-color:#47A0C1 !important;
}

.nav-tabs>li.active>a .badge.ng-binding {
    background:#222 !important;
    color:#fff !important;
}

.dropdown-toggle .ng-binding, .masthead .btn-create-room .fa, .masthead .btn-list-rooms .fa, .btn.btn-default.dropdown-toggle:hover .fa {
    color:#f1f1f1;
}

.btn-default, a.list-group-item, button.list-group-item, a.list-group-item .list-group-item-heading, .nav-tabs>li>a:hover, .pagination>li>a, .pagination>li>span {
    color:#47A0C1;
}

.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    background:#337ab7 !important;
}

.btn[disabled], .navbar-default .navbar-nav>.open>a>.ng-binding, .badge.ng-binding, .nav-tabs>li.active>a {
    color:#222 !important;
}

.navbar-default .navbar-nav>.open>a>.ng-binding>.badge, .dropdown-menu, .dropdown-menu a.ng-binding:hover .badge {
    background-color:#222 !important;
}


.dropdown-menu>li>a:hover {
    background-color:#47A0C1 !important;
    color:#222 !important;
}

.dropdown-menu>li:hover .fa {
    color:#222 !important;
}

.slider .track:hover, .nav-tabs, .btn-default, .nav-tabs>li.active>a, .slider, .pagination>li>a, .pagination>li>span {
    border-color:#000 !important;
}

.slider .leftTrack, .btn-default, .pagination>li>a, .pagination>li>span {
    background-color:#41454D !important;
}

.slider .statusTrack {
    background-color:#282A30 !important;
}

.slider .track {
    background-color:#1B1C24;
}
}
