@-moz-document domain("meduza.io") {
.App-root,
.GeneralMaterial-layout,
.EmbedMaterial-container,
.NewsBlock-root,
.CardMaterial-card,
.CardMaterial-nav,
.SlidesMaterial-slide,
.SlidesMaterial-root::before,
.BrandMaterial-container,
.src-scripts-components-Quiz-Quiz__root___3f06H {
    background-color: #1d1d1d;
}
.SimpleBlock-p,
.RelatedBlock-root a,
body,
.SourceBlock-origin,
.MediaBlock-is1to3 .MediaBlock-contentWrap,
.MediaBlock-is1to4 .MediaBlock-contentWrap,
.EmbedBlock-caption,
.IframeBlock-caption {
    color: #d8d8d8;
}
.RichTitle-root,
.CardTitle-title {
    color: white;
}
.RelatedBlock-root {
    background: #151515;
}
.ChronologyItem-root {
    background: #151515;
    color: white;
}
.SourceBlock-root,
.SimpleBlock-blockquote {
    background-color: #0e0e0e;
    color: #f1f1f1
}
.Footnote-root {
    background: #3a3a3a;
}
.UnderTheSun-root,
.SuperBlock-root,
.SlidesMaterial-bg {
    background-color: #252525 !important;
}
.InfoPanel-root {
    background: #2f2f2f;
}
.TopicBlock-root,
.MediaBlock-isPicture,
.MediaBlock-isVideo,
.MediaBlock-is1to3,
.MediaBlock-is1to4 {

    background: #191919;
}
.Messenger-header,
.Affiliate-root {
    background: #252525;
    border-style: none;
}
.Messenger-root {
    background: #2f2f2f;
}
.Topic-root {
    color: #fff;
}
.Topic-root:hover,
.Message-root:hover {
    background: #3a3a3a;
}
.Messenger-notify {
    background: #191919;
}
.Messages-rules {
    background: #232323;
}

.CardMaterial-card,
.SlidesMaterial-slide,
.SlidesMaterial-root::before {
    box-shadow: inset 0 0 0 1px rgba(0, 0, 0, .15), inset 0 0 0 1px rgba(0, 0, 0, .15), inset 0 -1px 0 0 rgba(0, 0, 0, .2);
}
.MaterialPanel-sticked {
    background-color: hsla(0, 0%, 10%, .85);
}
.MaterialPanel-ad > a {
    color: #d8d8d8
}
.SimpleBlock-blockquote a {
    color: #b88b58
}
::selection {
    background: #aaa;
}
span[data-body] {
    background: #27575a !important
}
span[data-body]:hover {
    background: #473737 !important
}

.EmbedMaterial-containerBackground {
    display: none;
}
.DFPBanner-side {
    visibility: hidden
}
