css:
@-moz-document domain("duckduckgo.com") {
:root
{
		/*User colors*/
		--main-colour: #EF4C3C;
		--main-background: #151515;
		--second-background: #222;
		--hover-background: #333;
		--main-text: #babfc4;
        --border-colour: #111;
}

body, .body--home, .site-wrapper, .region__body, .badge-link, .modal__box, .module--news__left, .module--news__right, .module--news__image-wrapper, .result__image, .map-detail, .vertical--map__sidebar, .vertical--map__sidebar__header, .zci {
    background-color: var(--main-background);
}
.header-wrap {
    background-color: var(--second-background);
}
.search__input--adv {
    color: var(--main-text);
}
.search--home, .search--header {
    background-color: var(--main-background);
    border-color: var(--border-colour);
}
.search__clear, .search__button, .search--home.has-text .search__clear {
    color: var(--main-text);
}
.search__button:hover, .search__button--active, .search:hover .search__button, .search__input:focus ~ .search__button, .search--header.has-text.search--hover .search__button, .search--header.has-text.search--focus .search__button, .search--home.has-text .search__button, .search__button:focus, .search:hover .search__button:focus, .search--header.has-text.search--hover, .search--header.has-text.search--focus .search__button:hover, .search--home.has-text .search__button:focus, .search--home.has-text .search__button:hover {
    background-color: var(--main-background);
}
.search-filters-wrap::before {
    background-image: linear-gradient(90deg, var(--main-background), rgba(20%, 20%, 20%, 0));
}
.search-filters-wrap::after {
    background-image: linear-gradient(90deg, rgba(20%, 20%, 20%, 0), var(--main-background));
}
.zcm__link:hover, .zcm__link:active, .zcm__link.is-active {
    background-color: var(--second-background);
    color: var(--main-text);
    border-bottom-color: var(--main-text);
}
.metabar__dropdowns-wrap::before {
    background-image: none;
}
.metabar__dropdowns-wrap::after {
    background-image: none;
}
.set-bookmarklet__input, .set-bookmarklet__data, .btn, .btn:visited, .btn:hover, .btn.is-active, .btn:active, .btn.btn--primary, .btn--primary:hover, .frm__input {
    background-color: inherit;
    border-color: #00000000;
    color: var(--main-text);
}
.set-bookmarklet__input, .set-bookmarklet__data, .btn:hover, .btn:visited, .btn:hover, .btn.is-active, .btn:active, .btn.btn--primary, .btn--primary:hover, .frm__input {
    color: var(--main-colour);
}
.result{
        border-left: solid 1px #00000000;
}
.result.highlight{
    border-color: var(--main-background) !important;
    border-radius: 0 !important;
    border-left: 1px solid !important;
    border-left-color: var(--main-text) !important;
    background-color: inherit !important;
    box-shadow: none !important;
}
}
