css:
@-moz-document domain("bb-enq.jp") {
/*======================================
  BB-enq ja jp compact
  ======================================*/
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: initial !important; font-weight: initial !important;
 line-height: 1.4em !important;
 }
#viewport, .container {width: auto !important;}
.container	{padding: 0 0.5em;}

#viewport {background: url(https://bb-enq.jp/member/img/cover2.png);}
#viewport>div {background-color: rgba(255,255,255,0.8);}


/*--- header ---*/
#header .logo {float: left;}
#header .logo a {height: auto;}
#header .account p {padding-right: 0;}
#header .account {position: static; float: right;}
#header .account .nickname + br {content: "";}
#header .account .points {margin: 0;}
#header .account a {width: auto; height: auto;}

/*--- footer ---*/


/*--- main contents ---*/
#side, #main {float: none; width: auto; margin: 0; padding: 0;}

.menu .item1, .menu .item9 {width: auto;}
.menu a {width: auto; height: auto; font-size: initial;}
.menu a span br {display: none;}

#cover {display: none;}

#side .repository {margin-bottom: 0;}
.repository li {display: table-cell;}
.repository li, .register li {width: auto; margin-bottom: 0;}
.repository a, .register a {height: auto;}
#side .function {margin-bottom: 0; text-indent: initial;}
#side .function li {width: auto; float: left;}

#main h1, #main h2, #main h3, #main h4, #main h5, #main h6, #main p, #main div, #main table, #main dl, #main ul, #main ol, #main form {margin-bottom: 0;}
#main {clear: both;}
#main > p br, #main > div br {display: none;}

}
