css:
@-moz-document domain("documentation.red-gate.com") {
html {
    background-color: #333 !important;
}

body, h1, h2, h3, h4, h5, h6, p, blockquote, pre, dl, dd, ol, ul, form, fieldset, legend, figure, caption, hr {
    margin: 0;
    padding: 0;
    color: white !important;
}
code {
    color:#9cd4ff;
}
h1 {
    margin-top: 20px;
}
h2 {
    margin-top:40px;
    margin-bottom:10px;
    color:#ff9f67 !important;
}
h3 {
    margin-top:20px;
    margin-bottom:10px;
}
body {
    color: #fff !important;
}
th, td, table {
    background: #444;
    color: white;
}
a {
    color: #8bbaff;
}

.typ {
    color: #fff;
}

.nav--vertical.nav--vertical--minimal a, .nav--vertical.nav--vertical--minimal a:hover {
    color: #fff;
}
.notification {
    background-color: #444;
}

.background-color--grey--1 {
    background-color: #444 !important;
}

.nav--vertical.nav--vertical--minimal {

    border-right: 5px solid #444 !important;
}

.home .tabbed__container li a {
    color: #fff;
}

.tabs a {

    color: #fff;
}

.panel {
    border: none;
    background: #444;
}

button, input, optgroup, select, textarea {
    color: white;
    background: #222;
}

.space-header {
    border-bottom: 0px solid #ebebeb00;
}

p.code__title {
    background-color: #333;
}

pre.prettyprint {
    background: #444;
    border: 0px;
}

.pln {
    color: #dca2ff;
}

.pun {
    color: #2aacff;
}
.com {
    color: #2c952d;
}
}
