css:
@-moz-document url-prefix("https://play.typeracer.com/"), url-prefix("https://data.typeracer.com/") {
.themeHeader,
.mainUserHeader {
    background: 0;
    background-color: #2d55a2;
}

body {
    background: #e5e5e5;
}

.container {
    background: #2d55a2;
}
a {
    color: #426464;
    font-weight: 400 !important;
    transition: all 75ms ease-in-out;
}
.footer a {
    color: black;
}


a:hover {
    color: #65819c;
}
.themeHeader a {
    color: #FFF;
}
.themeHeader a:focus {
    color: #EEE !important;
}
.themeHeader a:hover {
    background: none;
    color: #a5bfd7;
}

.main {
    background-color: #e5e5e5;
    background-image: none;
}
.navHome,
.navTexts,
.navCompetitions,
.navHistory,
.navUpgrade,
.navMessages,
.navProfile,
.navFriends {
    background: none !important;
}

.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: #EEE;
}

.progressBar {
    border: 1px #426464 !important;
    border-bottom-style: dashed !important;
} 


.scoreboard .row .rankPanel {
    z-index: 1;
}
.scoreboard > tbody > tr > td > table > tbody > tr > td {
    overflow: hidden;
}
.scoreboard .row .rankPanel .rankPanelWpm-self {
    font-size: 120%;
}
.mainViewport .room .gameView {
    transition: all 2s linear;
}
input:focus {
    outline: none;
}


.footer {
    background: none;
    background-color: #e5e5e5;
    ;
    color: black;
    transition: all 75ms ease-in-out;
}
.footer a:hover {
    color: #a7acd7;
    text-decoration: none;
    transition: all 75ms ease-in-out;
}
.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
    background: 0;
}

.eastWidget,
.newEastWidget,
.newWestWidget,
.newNorthWidget,
.mainMenuAdUnit,
#hostpageBannerAds {
    display: none !important;
}

.gwt-TabBar .gwt-TabBarItem {
    color: #426464;
}
.PopupWithIcon {
    background-color: #003966;
    border-color: #292b2c;
}
.trPopupDialog .xButton {
    background-color: #003966;
}
.trPopupDialog {
    box-shadow: 1px 2px 5px #333;
}
.fullTextStr {
    color: #292b2c !important;
    background-color: #FFF !important;
}

.practiceViewScoresPopup {
    display: none;
}
.gwt-MenuBar {
    background: #f9f9f9;
}
.popupContent {
    background: #c3d1ea;
}

.InstaGibPopup {
    display: none;
}

.nonHideableWords,
.inputPanel span {
    font-size: 22px !important;
    text-decoration: none;
}

.mainViewportHolder,
.mainViewport {
    width: 700px;
}

.gwt-TabBar .gwt-TabBarItem {
    color: #426464;
}
.gwt-TabBar .gwt-TabBarItem:hover,
.gwt-TabBar .gwt-TabBarItem:focus {
    color: #65819c;
}

.gameView .raceAgainLink-flashOn {
    color: #65819c
}
}
