css:
@-moz-document url-prefix(https://www.nicovideo.jp/watch/)
{
#siteHeader #siteHeaderInner{width:99%!important;display:none!important;}.WatchAppContainer-switchToFlash{display:none!important;}.WatchAppContainer{padding-top:0!important;}

div.HeaderContainer-searchBox{display:none!important;}

div.MarqueeContainer{display:none!important;}

div.WatchAppContainer-main div.MainContainer div.MainContainer-player div.PlayerContainer div.VideoContainer{width:960px!important;height:540px!important;margin:20px auto!important;background-color:#000000!important;}

body.is-fullscreen div.WatchAppContainer-main div.MainContainer div.MainContainer-player div.PlayerContainer div.VideoContainer{width:100vw!important;height:100vh!important;margin:0 auto!important;background-color:#000000!important;}

div.WatchAppContainer-main div.MainContainer{position:absolute!important;top:0!important;left:0!important;width:100%!important;margin:0!important;}div.WatchAppContainer-main div.MainContainer div.MainContainer-player{width:100%!important;}

div.MainContainer div.ControllerBoxContainer{position:fixed!important;bottom:-32px!important;width:100%!important;}div.CommentPostContainer{position:absolute!important;bottom:73px!important;}.SeekBarContainer{padding-left:0!important;padding-right:0!important;}

div.WatchAppContainer-main div.HeaderContainer{position:absolute!important;top:580px!important;width:1024px!important;height:calc(100% - 655px)!important;}

div.VideoDescriptionContainer-expander{display:none!important;}div.HeaderContainer-videoTitle{padding:0!important;}div.HeaderContainer-row{width:500px!important;}div.VideoDescriptionContainer{position:absolute!important;top:85px!important;right:0!important;width:500px!important;padding-left:30px!important;border-left:1px solid #666!important;height:calc(100% - 92px)!important;margin-bottom:0!important;overflow-y:scroll!important;}div.VideoDescriptionContainer *{font-size:17px!important;}div.HeaderContainer div.Grid HeaderContainer-row .GridCell{display:block!important;}div.HeaderContainer div.Grid HeaderContainer-row .Grid{display:block!important;}div.HeaderContainer div.HeaderContainer-row div.VideoOwnerInfo-gridCell{position:fixed!important;top:597px!important;left:calc(50% + 44px)!important;}

div.HeaderContainer div.TagContainer{width:500px!important;margin:25px 0 0 0 !important;padding:0!important;}div.HeaderContainer div.HeaderContainer-row div.VideoMetaContainer{height:auto!important;width:500px!important;}div.VideoUploadDateMeta{min-width:132px!Important;border:none!important;box-shadow:none!important;}.VideoUploadDateMeta-title{margin:0 12px 4px 0!important;}.VideoUploadDateMeta-dateTimeLabel{margin:0 12px 4px 0!important;}div.VideoViewCountMeta{min-width:86px!Important;}div.CommentCountMeta{min-width:86px!Important;}div.MylistCountMeta{min-width:101px!Important;}div.CategoryRankMeta{display:none!important;}div.VideoMetaOverflowMenuContainer{display:none!important;}.VideoOwnerInfo-links{padding:0 8px 0 8px!important;}

.CommentPanelContainer{display:none!important;}
.MainContainer-commentPanel,.VideoMenuContainer,.VideoMenuContainer-areaLeft{background:transparent!important;}
.VideoMenuContainer-areaRight{display:none!important;}
.VideoMenuContainer-areaLeft .UadButton,.VideoMenuContainer-areaLeft .VideoMenuContainer-uadBalloonWrapper{display:none!important;}
.VideoMenuContainer{position:absolute!important;bottom:-55px!important;left:326px!important;width:124px!important;}
.VideoMenuContainer:after{border:none!important;}
.VideoMenuContainer-areaLeft>*{margin-right:15px!important;}
.MiniBannerAdContainer{border:none!important;}
.MainContainer-commentPanel{left:50%!important;}

div.BottomContainer{display:none!important;}

div.FooterContainer{display:none!important;}

div.CommentPostContainer-commentShutter{display:none!important;}

body,html{height:auto!important;}

#CommentRenderer canvas{width:960px!important;height:540px!important;}
body.is-fullscreen #CommentRenderer canvas{width:100%!important;height:100%!important;}

.GeneralNoticeContainer-errorMessage{display:none!important;}

.CommentPostContainer-commandInput{width:0!important;display:none!important;}



}
