input[type=checkbox] {
         position: relative;
	       cursor: pointer;
    }
    input[type=checkbox]:before {
         content: "";
         display: block;
         position: absolute;
         width: 10px;
         height: 10px;
         top: 0;
         left: 0;
         border: 2px solid #555555;
         border-radius: 1px;
         background-color: #202020;
}
    input[type=checkbox]:checked:after {
         content: "";
         display: block;
         width: 3px;
         height: 6px;
         border: solid #00bb00;
         border-width: 0 2px 2px 0;
         -webkit-transform: rotate(45deg);
         -ms-transform: rotate(45deg);
         transform: rotate(45deg);
         position: absolute;
         top: 2px;
         left: 5px;
}

