css:
@-moz-document regexp(".*logos\\.com.*|.*reviews\\.faithlife\\.com.*") {
/* brand-bar */
#hd .brand-bar-container

	{ border-bottom: 1px solid #000; background: #111; }


/* mega-menu */
.mega-menu-pane 

	{ background: #222 !important; border: 1px solid black !important; }


.mega-menu-close 

	{ display: none; }


.site-nav .menu-category a.hover 
	
	{ background-color: #333; }
	

/* site-wide */

body

	{ background: #222 !important; }

.section, .account-page, .large-profile, .profile-large, .show-more, #ft.footer, #sidebar #product-recommendations .small-product-list>li.show-more, #sidebar, #content, #reviews-wrapper, #reviews-wrapper #reviews-body .news-feed, .news-feed__item, #search-display-header #search-context-tabs .selected

	{ background: transparent !important; }


#sidebar #product-recommendations .small-product-list>li:hover, #search-display-header #search-context-tabs li

	{ background: rgba(0,0,0,0.2); }


/* borders bottom */
#hd, .header, .profile-large, .site-notifications-list li, #ajaxnotifications li, #product-details .details, .news-feed__item, .post-info, .menu-secondary, .sidebar .social-ul, .sidebar .email-form

	{ border-bottom: 1px solid #333; }

		.search-results .header { border-bottom: 0; }


/* borders top */
.account-page .widget-container .widget, #sidebar #product-recommendations>ul>li+li, #reviews-wrapper #reviews-body .news-feed, .search-results.list>li+li

	{ border-top: 1px solid #333; }


/* borders left */
.widget-sidebar, #account-main, #sidebar-left #main, #checkout #main, #account #main, #ordersummary #main, #product-search #main, #products #main, #groups #main

	{ border-left: 1px solid #333 !important; }


/* borders right */
#account-sidebar, #product-details .product-description

	{ border-right: 1px solid #333; }
	

#sidebar #product-recommendations 

	{ border-left: 0; }


/* border-color */
hr, #reviews-wrapper #reviews-body .news-feed .comment-editor, #reviews-wrapper #reviews-body .news-feed .news-feed__item, #reviews-wrapper #reviews-body .news-feed .replies .reply-main
	
	{ border-color: #333 !important; }


/* black text  to light text */
#phone-number a, .site-nav .menu-category h2, .site-nav .menu-tab>h2, .news-feed__item article .options .account-link, .news-feed__item article .options .user-link, .news-feed__item article .user-link

	{ color: #ccc; }


/* dark, non-black text  to light text */
.default-billing-and-shipping .default-shipping-widget .name, .sign-in-dropdown .header-text, .profile-large .name, .reviews .review-container h2 a, body, p, button, input, textarea, h1, h2, h3, h4, h5, h6, .l7base .product__title, .search-results.list .search-result p, #reviews-wrapper #reviews-body #review-editor .account-details .user-link, p, li
	
	{ color: #aaa; }


/* dark links to link color */
.widget ul li a

	{ color: #006ced !important; }

.widget ul li a:hover 

	{ color: #ccc !important; }


/* accent bg */
#account-sidebar #sidebar-nav .active a, #sidebar #product-recommendations h2, #sidebar #credit-recommendations h2, #sidebar #gift-purchase-sidebar-header h2, #facet-browsing-control .header

	{ background: transparent;}


/* input */
#search input, .reviews .review-container .buttons-container .review-textbox, .l7base .form__input, input, select, textarea

	{ background: #aaa; border-color: #aaa; color: #000; }


/* product-image */
#product-details .details #image-container 

	{ background-color: transparent; border: 0; }


/* lightbox */
div.logos

	{ background-color: #222;}
}
